ingredients_array.push( [ 'CostanzoRecipes_Entrees_SweetPotatoHash', '<p><ul><li onclick="handleItemClick()">2 medium sweet potatoes, peeled and cut into ½ inch cubes </li><li onclick="handleItemClick()">½ teaspoon salt </li><li onclick="handleItemClick()">1 tablespoon butter </li><li onclick="handleItemClick()">2 cups button mushrooms, sliced </li><li onclick="handleItemClick()">1 tablespoon grapeseed oil </li><li onclick="handleItemClick()">½ red onion, diced </li><li onclick="handleItemClick()">Optional: 1 Fresno chili or jalapeno, diced (remove seeds for a more mild heat) </li><li onclick="handleItemClick()">2 to 3 cloves garlic, minced </li><li onclick="handleItemClick()">1 red bell pepper, chopped </li><li onclick="handleItemClick()">Salt and pepper to taste </li><li onclick="handleItemClick()">2 tablespoons fresh cilantro, minced </li><li onclick="handleItemClick()">1 tablespoon grapeseed oil </li><li onclick="handleItemClick()">4 to 8 eggs </li><li onclick="handleItemClick()">Optional: ketchup and/ or Sriracha sauce for serving </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Entrees_SweetPotatoHash', '<p><ol><li onclick="handleItemClick()">To par-boil the sweet potatoes place them in a medium-sized pot and cover with water, add the ½ teaspoon of salt, stir. Bring to a boil, uncovered, and boil for 2 to 3 minutes. The potatoes should be slightly tender, but not completely cooked. A fork should go about ⅓ of the way into the potato. Once they are par-boiled immediately drain them in to a colander and rinse them in cold water to stop the cooking process. Set aside. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1602\')" class="alarm-clock-icon"><div id="circular-timer-1602" class="circular-timer-container"><script>new CircularTimer("circular-timer-1602", 120)</script><script>new CircularTimer("circular-timer-1602", 180)</script></li><li onclick="handleItemClick()">While the sweet potatoes are coming to a boil sauté the mushrooms. Heat a large, heavy-bottomed skillet over medium-high heat. Melt the butter. Add the mushrooms and sauté them for about 10 minutes, stirring occasionally. They should be browned and tender. Remove the mushrooms into a dish and set aside. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1603\')" class="alarm-clock-icon"><div id="circular-timer-1603" class="circular-timer-container"><script>new CircularTimer("circular-timer-1603", 600)</script></li><li onclick="handleItemClick()">Heat the grapeseed oil in the heated skillet, add the onion and the Fresno chili or jalapeno (optional). Sauté until tender, about 3 minutes. Add the garlic and sauté for 1 minute more. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1604\')" class="alarm-clock-icon"><div id="circular-timer-1604" class="circular-timer-container"><script>new CircularTimer("circular-timer-1604", 180)</script><script>new CircularTimer("circular-timer-1604", 60)</script></li><li onclick="handleItemClick()">Add the red bell pepper and the par-boiled sweet potatoes. Season with a healthy pinch of salt and pepper. Sauté for about 10 to 15 minutes, stirring occasionally. You will know the potatoes are done when they are fork-tender and slightly caramelized. Add the mushrooms back into the hot pan and allow to heat for 2 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1605\')" class="alarm-clock-icon"><div id="circular-timer-1605" class="circular-timer-container"><script>new CircularTimer("circular-timer-1605", 600)</script><script>new CircularTimer("circular-timer-1605", 900)</script><script>new CircularTimer("circular-timer-1605", 120)</script></li><li onclick="handleItemClick()">Remove the pan from the heat and stir in the cilantro. Taste-test and add more salt and pepper as needed. Transfer the sweet potato mixture onto a serving platter or individual plates. </li><li onclick="handleItemClick()">Return to the skillet, heat over medium-high heat. Add a tablespoon of grapeseed oil. Crack as many eggs as you plan to use into the skillet. Cover and cook for about 3 to 5 minutes, or until the egg white has set and the yolk is still runny. NOTE: you can cook your eggs any way you prefer, but I recommend eggs that have runny yolks for this recipe so that the potatoes can soak everything up. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1606\')" class="alarm-clock-icon"><div id="circular-timer-1606" class="circular-timer-container"><script>new CircularTimer("circular-timer-1606", 180)</script><script>new CircularTimer("circular-timer-1606", 300)</script></li><li onclick="handleItemClick()">Serve the eggs over the potato hash mixture with a side of ketchup and Sriracha sauce (optional). </li></ol>' ] );
credits_array.push( [ 'CostanzoRecipes_Entrees_SweetPotatoHash', '<p>If you want to make this breakfast go even faster on the morning of then you can make the sweet potato mixture the night before, store it in the refrigerator in an airtight container and heat it up while you cook up your eggs. The sweet potato mixture reheats nicely. <p><ul><li onclick="handleItemClick()"><a href="https://robustrecipes.com/sweet-potato-hash-with-mushrooms/" target="_blank">https://robustrecipes.com/sweet-potato-hash-with-mushrooms/</a> </li><li onclick="handleItemClick()">date created: [2019-10-29 Tue]  </li></ul>' ] );
ingredients_array.push( [ 'CostanzoRecipes_Entrees_SweetPotatoBlackBeanBurgers', '<p><ul><li onclick="handleItemClick()">1 medium sweet potato </li><li onclick="handleItemClick()">1 15-ounce can black beans, drained and rinsed </li><li onclick="handleItemClick()">¼ cup chopped onion </li><li onclick="handleItemClick()">½ cup rolled oats </li><li onclick="handleItemClick()">1 teaspoon ground cumin </li><li onclick="handleItemClick()">1 teaspoon paprika </li><li onclick="handleItemClick()">½ teaspoon onion powder </li><li onclick="handleItemClick()">½ teaspoon garlic powder </li></ul><h3 onclick="toggleSection(\'1217\')">Sriracha Mayo (optional)<span id="heading-ellipses-1217"></h3><div id=\'heading-content-1217\'><p><ul><li onclick="handleItemClick()">½ cup vegan mayo </li><li onclick="handleItemClick()">1 tablespoon sriracha hot sauce </li><li onclick="handleItemClick()">pinch garlic powder </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Entrees_SweetPotatoBlackBeanBurgers', '<p><ol><li onclick="handleItemClick()">Preheat oven to 400°. </li><li onclick="handleItemClick()">Cut potato in half lengthwise and place on pan. </li><li onclick="handleItemClick()">Bake for 30 minutes until fork tender; remove from oven. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1600\')" class="alarm-clock-icon"><div id="circular-timer-1600" class="circular-timer-container"><script>new CircularTimer("circular-timer-1600", 1800)</script></li><li onclick="handleItemClick()">Lower oven to 375°. </li><li onclick="handleItemClick()">When potato is cool enough to handle, peel off skin. </li><li onclick="handleItemClick()">Add flesh to large bowl and mash. </li><li onclick="handleItemClick()">Add black beans and mash lightly into potato, leaving some beans whole. </li><li onclick="handleItemClick()">Stir in onion, oats, cumin, paprika, onion powder and garlic powder. </li><li onclick="handleItemClick()">Form burgers. </li><li onclick="handleItemClick()">Lightly oil a pan. </li><li onclick="handleItemClick()">Fry 15 minutes on each side. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1601\')" class="alarm-clock-icon"><div id="circular-timer-1601" class="circular-timer-container"><script>new CircularTimer("circular-timer-1601", 900)</script></li><li onclick="handleItemClick()">Sever on rolls or over salad. </li></ol>' ] );
