ingredients_array.push( [ 'CostanzoRecipes_Entrees_SunDriedTomatoandKalePasta', '<p><ul><li onclick="handleItemClick()">½ cup olive oil </li><li onclick="handleItemClick()">1 bunch tuscan kale ribs removed and roughly chopped </li><li onclick="handleItemClick()">8 cloves garlic finely minced </li><li onclick="handleItemClick()">1 teaspoon red pepper flakes </li><li onclick="handleItemClick()">1 lemon zested and juiced </li><li onclick="handleItemClick()">½ cup sun dried tomatoes the kind in oil, roughly chopped </li><li onclick="handleItemClick()">¾ cup white wine </li><li onclick="handleItemClick()">10 ounces spaghetti </li><li onclick="handleItemClick()">¾ cup shredded mozzarella </li><li onclick="handleItemClick()">kosher salt and freshly cracked black pepper to taste </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Entrees_SunDriedTomatoandKalePasta', '<p><ol><li onclick="handleItemClick()">Heat the olive oil over medium heat in a skillet. Once hot, add the kale, red pepper flakes and season with salt and pepper and cook until wilted. </li><li onclick="handleItemClick()">Add the garlic and sauté for 30 seconds until fragrant. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1599\')" class="alarm-clock-icon"><div id="circular-timer-1599" class="circular-timer-container"><script>new CircularTimer("circular-timer-1599", 30)</script></li><li onclick="handleItemClick()">Add the sun dried tomatoes, lemon zest and stir to combine. </li><li onclick="handleItemClick()">Add the white wine and cook until alcohol cooks off. </li><li onclick="handleItemClick()">Remove from the heat and stir in the lemon juice. Stir to combine, taste and adjust seasoning as needed. </li><li onclick="handleItemClick()">Cook the pasta according to the package directions. Reserve ½ cup of the pasta water. </li><li onclick="handleItemClick()">Drain and toss with sauce, reserved water, cheese and season with salt and pepper as needed. </li><li onclick="handleItemClick()">Serve immediately. </li></ol>' ] );
