ingredients_array.push( [ 'CostanzoRecipes_Entrees_SpicyBlackBeanCakes', '</div>  <!-- <h3> --><h3 onclick="toggleSection(\'1211\')">Ingredients:<span id="heading-ellipses-1211"></h3><div id=\'heading-content-1211\'><p><ul><li onclick="handleItemClick()">¼ cup quinoa, dry </li><li onclick="handleItemClick()">2 (14.5 ounce) cans black beans, drained and rinsed </li><li onclick="handleItemClick()">2 tbsp olive oil </li><li onclick="handleItemClick()">6 cloves garlic, pressed </li><li onclick="handleItemClick()">2 fresh jalapeno peppers, finely diced and/or </li><li onclick="handleItemClick()">1 chipotle jalapeno peppers, in adobo sauce </li><li onclick="handleItemClick()">1 tablespoon ground cumin </li><li onclick="handleItemClick()">1 tablespoon chili pepper </li><li onclick="handleItemClick()">salt and black pepper to taste </li><li onclick="handleItemClick()">1 egg, lightly beaten </li><li onclick="handleItemClick()">½ cup plain dried bread crumbs (optional) </li><li onclick="handleItemClick()">½ cup corn kernels (optional) </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Entrees_SpicyBlackBeanCakes', '</div>  <!-- <h3> --><h3 onclick="toggleSection(\'1212\')">Method:<span id="heading-ellipses-1212"></h3><div id=\'heading-content-1212\'><p><ol><li onclick="handleItemClick()">To prepare lime sour cream, mix the sour cream, lime juice, 1 small minced jalapeno, and salt together in a small bowl. Cover, and refrigerate. </li><li onclick="handleItemClick()">Preheat oven to 375 degrees. </li><li onclick="handleItemClick()">Cook the quinoa per instructions on package.  (Toast quinoa first, rinse then simmer ¼ cup quinoa in ½ cup boiling water.) This will take about 10-15 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1588\')" class="alarm-clock-icon"><div id="circular-timer-1588" class="circular-timer-container"><script>new CircularTimer("circular-timer-1588", 600)</script><script>new CircularTimer("circular-timer-1588", 900)</script></li><li onclick="handleItemClick()">Puree black beans with olive oil, garlic, peppers and spices in a food processor. </li><li onclick="handleItemClick()">Whisk egg in a medium-sized bowl. </li><li onclick="handleItemClick()">Add black bean puree, cooked quinoa and bread crumbs (if using) to egg bowl and mix until fully combined into a batter. </li><li onclick="handleItemClick()">Place 6 cupcake papers on a baking sheet.  Divide black bean cake batter among the 6 cups, and bake for about 15 to 20 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1589\')" class="alarm-clock-icon"><div id="circular-timer-1589" class="circular-timer-container"><script>new CircularTimer("circular-timer-1589", 900)</script><script>new CircularTimer("circular-timer-1589", 1200)</script></li><li onclick="handleItemClick()">Allow black bean cakes to cool for a few minutes.  Remove from papers.  Serve over a bed of spinach with fresh salsa and grated cheese. Or over polenta or mashed potatoes. </li><li onclick="handleItemClick()">Serve with lime sour cream. </li></ol>' ] );
