methods_array.push( [ 'CostanzoRecipes_Entrees_SourdoughPasta', '<p><ol><li onclick="handleItemClick()">Pour all the ingredients into the bowl of a stand mixer with a dough hook attachment and mix on low until it forms a ball. </li><li onclick="handleItemClick()">Cover the dough in plastic and rest the dough for 30 minutes (up to overnight). <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1583\')" class="alarm-clock-icon"><div id="circular-timer-1583" class="circular-timer-container"><script>new CircularTimer("circular-timer-1583", 1800)</script></li><li onclick="handleItemClick()">When ready to make the pasta, set the dough onto a floured surface and roll out with a rolling pin to get it as thin as possible (15-20 minutes). <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1584\')" class="alarm-clock-icon"><div id="circular-timer-1584" class="circular-timer-container"><script>new CircularTimer("circular-timer-1584", 900)</script><script>new CircularTimer("circular-timer-1584", 1200)</script></li><li onclick="handleItemClick()">Slice noodles to your desired thickness. </li><li onclick="handleItemClick()">Drop noodles into boiling water for 3-5 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1585\')" class="alarm-clock-icon"><div id="circular-timer-1585" class="circular-timer-container"><script>new CircularTimer("circular-timer-1585", 180)</script><script>new CircularTimer("circular-timer-1585", 300)</script></li><li onclick="handleItemClick()">Drain and serve. </li></ol>' ] );
credits_array.push( [ 'CostanzoRecipes_Entrees_SourdoughPasta', '<p><ul><li onclick="handleItemClick()">From <a href="https://iamhomesteader.com/sourdough-pasta/#wprm-recipe-container-65130" target="_blank">https://iamhomesteader.com/sourdough-pasta/#wprm-recipe-container-65130</a> </li><li onclick="handleItemClick()">date created: [2022-12-31 Sat]  </li><li onclick="handleItemClick()">[2022-12-31 Sat] I was worried that the dough was very sticky, but it was well-behaved in the pasta machine. The noodles were soft and tasty, reminiscent of egg noodles. </li></ul>' ] );
ingredients_array.push( [ 'CostanzoRecipes_Entrees_SmokyGreensandBeans', '<p>Prep Time: 30 min <p>Total Time: 30 min <p><ul><li onclick="handleItemClick()">2 tablespoons olive oil </li><li onclick="handleItemClick()">1 large onion, chopped </li><li onclick="handleItemClick()">2 garlic cloves, chopped </li><li onclick="handleItemClick()">1 14½-ounce can diced tomatoes in juice </li><li onclick="handleItemClick()">1½ teaspoons smoked paprika </li><li onclick="handleItemClick()">1 14½-ounce can vegetable broth </li><li onclick="handleItemClick()">8 cups coarsely chopped assorted greens (such as kale, mustard greens, and collard greens about 8 ounces) </li><li onclick="handleItemClick()">1 15-ounce can cannellini (white kidney beans), drained </li><li onclick="handleItemClick()">Grated Manchego or Parmesan cheese (optional) </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Entrees_SmokyGreensandBeans', '<p><ol><li onclick="handleItemClick()">Heat oil in heavy large pot over medium-high heat. </li><li onclick="handleItemClick()">Add onion and sauté until soft and beginning to brown, about 6 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1578\')" class="alarm-clock-icon"><div id="circular-timer-1578" class="circular-timer-container"><script>new CircularTimer("circular-timer-1578", 360)</script></li><li onclick="handleItemClick()">Add garlic; stir 1 minute. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1579\')" class="alarm-clock-icon"><div id="circular-timer-1579" class="circular-timer-container"><script>new CircularTimer("circular-timer-1579", 60)</script></li><li onclick="handleItemClick()">Add tomatoes with juice and paprika; stir 1 minute. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1580\')" class="alarm-clock-icon"><div id="circular-timer-1580" class="circular-timer-container"><script>new CircularTimer("circular-timer-1580", 60)</script></li><li onclick="handleItemClick()">Add broth and greens; bring to boil, stirring often. </li><li onclick="handleItemClick()">Reduce heat to medium-low, cover, and simmer until greens are wilted and tender, stirring occasionally, about 15 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1581\')" class="alarm-clock-icon"><div id="circular-timer-1581" class="circular-timer-container"><script>new CircularTimer("circular-timer-1581", 900)</script></li><li onclick="handleItemClick()">Stir in beans and simmer 1 minute to heat through. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1582\')" class="alarm-clock-icon"><div id="circular-timer-1582" class="circular-timer-container"><script>new CircularTimer("circular-timer-1582", 60)</script></li><li onclick="handleItemClick()">Divide among bowls; sprinkle with cheese, if desired. </li></ol>' ] );
