ingredients_array.push( [ 'CostanzoRecipes_Entrees_RoastedWinterVegetableJambalaya', '<p><ul><li onclick="handleItemClick()">½ cup brown rice, rinsed </li><li onclick="handleItemClick()">2 large carrots </li><li onclick="handleItemClick()">2 cup parsnips, peeled and diced </li><li onclick="handleItemClick()">2 cup, cubes sweet potato </li><li onclick="handleItemClick()">2 cup, diced Yukon gold potatoes </li><li onclick="handleItemClick()">1 medium onion, peeled and medium dice </li><li onclick="handleItemClick()">2 tbsp extra virgin olive oil </li><li onclick="handleItemClick()">1½  tsp kosher salt, divided </li><li onclick="handleItemClick()">1 tsp cayenne pepper, divided </li><li onclick="handleItemClick()">1 tsp ginger </li><li onclick="handleItemClick()">1 tsp paprika </li><li onclick="handleItemClick()">1 tsp turmeric </li><li onclick="handleItemClick()">½ tsp chili powder </li><li onclick="handleItemClick()">½ tsp cinnamon </li><li onclick="handleItemClick()">¼ tsp nutmeg </li><li onclick="handleItemClick()">¼ tsp black pepper </li><li onclick="handleItemClick()">1 tbsp tomato paste </li><li onclick="handleItemClick()">3 cup low-sodium vegetable broth </li><li onclick="handleItemClick()">3 tomatoes, medium dice </li><li onclick="handleItemClick()">1 cup parsley, divided </li><li onclick="handleItemClick()">15 fl oz garbanzo beans, can, no sodium </li><li onclick="handleItemClick()">15 fl oz pinto beans, can, no sodium </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Entrees_RoastedWinterVegetableJambalaya', '<p><ol><li onclick="handleItemClick()">Rinse rice in a strainer under cold water then place it in a small pot and add a little more than 1 cup of water. Cover pot and bring to a boil over high heat, then reduce to a simmer with the lid on for 30 to 35 minutes or until water is fully absorbed. Remove from heat. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1574\')" class="alarm-clock-icon"><div id="circular-timer-1574" class="circular-timer-container"><script>new CircularTimer("circular-timer-1574", 1800)</script><script>new CircularTimer("circular-timer-1574", 2100)</script></li><li onclick="handleItemClick()">While rice is cooking, preheat the oven to 400° F. </li><li onclick="handleItemClick()">Toss carrots, parsnips, both potatoes and onion in olive oil, 1 teaspoon salt, ½ teaspoon black pepper and spices until thoroughly coated. Spread evenly over a rimmed baking sheet and roast in the oven, stirring halfway through, until veggies are tender and lightly browned, about 25 to 30 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1575\')" class="alarm-clock-icon"><div id="circular-timer-1575" class="circular-timer-container"><script>new CircularTimer("circular-timer-1575", 1500)</script><script>new CircularTimer("circular-timer-1575", 1800)</script></li><li onclick="handleItemClick()">Transfer roasted veggies to a large pot or Dutch oven, add the tomato paste, and stir to coat. Cook, stirring often, until the raw tomato flavor has cooked off, about 1 to 2 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1576\')" class="alarm-clock-icon"><div id="circular-timer-1576" class="circular-timer-container"><script>new CircularTimer("circular-timer-1576", 60)</script><script>new CircularTimer("circular-timer-1576", 120)</script></li><li onclick="handleItemClick()">Add the chopped tomatoes, broth, half of the parsley and remaining salt and pepper. Bring to a boil over high heat, then reduce heat and simmer for 10 minutes. Stir in the chickpeas, pinto beans and rice and cook until the flavors have combined, about 5 minutes more. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1577\')" class="alarm-clock-icon"><div id="circular-timer-1577" class="circular-timer-container"><script>new CircularTimer("circular-timer-1577", 600)</script><script>new CircularTimer("circular-timer-1577", 300)</script></li><li onclick="handleItemClick()">Ladle the jambalaya into bowls, sprinkle with the remaining parsley and serve. </li></ol>' ] );
