ingredients_array.push( [ 'CostanzoRecipes_Entrees_Pizza', '<h3 onclick="toggleSection(\'1202\')">Ingredients:<span id="heading-ellipses-1202"></h3><div id=\'heading-content-1202\'><p><ul><li onclick="handleItemClick()">1 cup warm water </li><li onclick="handleItemClick()">1 pkg dry yeast </li><li onclick="handleItemClick()">3¼ cups (390 grams) unbleached flour </li><li onclick="handleItemClick()">½ tsp salt </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Entrees_Pizza', '</div>  <!-- <h3> --><h3 onclick="toggleSection(\'1203\')">Method:<span id="heading-ellipses-1203"></h3><div id=\'heading-content-1203\'><p><ol><li onclick="handleItemClick()">Soften yeast in water. </li><li onclick="handleItemClick()">Add salt then start adding flour until a wet dough forms. Knead briefly, just to form a consistent dough. Place ball of dough in a large plastic Ziploc bag (or large bowl) that has been coated with olive oil. Place in refrigerator for 4-5 hours. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1561\')" class="alarm-clock-icon"><div id="circular-timer-1561" class="circular-timer-container"><script>new CircularTimer("circular-timer-1561", 14400)</script><script>new CircularTimer("circular-timer-1561", 18000)</script></li><li onclick="handleItemClick()">About the time you turn the oven on to pre-heat, pull dough out of refrigerator. Punch down and then let rest for 10 minutes. Divide ball in half and coat well with flour. Start patting each ball of dough out into a circle, the thinner the better, letting rest if it won\'t spread out. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1562\')" class="alarm-clock-icon"><div id="circular-timer-1562" class="circular-timer-container"><script>new CircularTimer("circular-timer-1562", 600)</script></li><li onclick="handleItemClick()">Place an oven rack at the very lowest position; remove other racks and set aside. Place baking tiles or pizza stone on the rack and preheat the oven to 500 F for an hour or so. Make sure stone is in the oven from the beginning otherwise the high heat might crack it. </li></ol>' ] );
ingredients_array.push( [ 'CostanzoRecipes_Entrees_Pizza', '</div>  <!-- <h3> --><h3 onclick="toggleSection(\'1204\')">Ingredients:<span id="heading-ellipses-1204"></h3><div id=\'heading-content-1204\'><p><ul><li onclick="handleItemClick()">1 can (28 ounces) Italian plum tomatoes - buy the best you can find </li><li onclick="handleItemClick()">2 Tbl. tomato paste </li><li onclick="handleItemClick()">1 or 2 garlic cloves, minced or pressed </li><li onclick="handleItemClick()">1 tsp. dried oregano </li><li onclick="handleItemClick()">Salt and freshly ground black pepper </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Entrees_Pizza', '</div>  <!-- <h3> --><h3 onclick="toggleSection(\'1205\')">Method:<span id="heading-ellipses-1205"></h3><div id=\'heading-content-1205\'><p><ol><li onclick="handleItemClick()">Crush the tomatoes with your hands or a fork and place in a heavy saucepan over medium heat. Stir in the tomato paste, garlic, oregano and salt and pepper to taste. Bring the mixture just to a boil, then reduce the heat to low and simmer the sauce, stirring occasionally, until thick and flavorful, 30 minutes to 1 hour. Remove from the heat. (This may be stored in the refrigerator for up to 1 week or frozen up to 4 months) <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1563\')" class="alarm-clock-icon"><div id="circular-timer-1563" class="circular-timer-container"><script>new CircularTimer("circular-timer-1563", 1800)</script><script>new CircularTimer("circular-timer-1563", 3600)</script></li><li onclick="handleItemClick()">To bake: Turn the smoke alarm off. Cover pizza peel with coarse corn meal. Place one flattened piece of the dough onto the peel, spread with olive oil, cover with a thick layer of tomato sauce then add toppings. Sprinkle on a thick layer of parmesan cheese then drizzle with olive oil. Slide pizza onto the hot stone and bake 10 to 15 minutes until the top of the pie is beginning to brown. Remove pizza from oven and place on a cutting board. Let sit for a few minutes then slice and eat while hot. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1564\')" class="alarm-clock-icon"><div id="circular-timer-1564" class="circular-timer-container"><script>new CircularTimer("circular-timer-1564", 600)</script><script>new CircularTimer("circular-timer-1564", 900)</script></li></ol>' ] );
