methods_array.push( [ 'CostanzoRecipes_Entrees_PizzaNewYorkstylev20', '<p><ul><li onclick="handleItemClick()">Mix ingredients. </li><li onclick="handleItemClick()">Adding flour as necessary </li><li onclick="handleItemClick()">Knead until window pane thin, perhaps for 10 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1559\')" class="alarm-clock-icon"><div id="circular-timer-1559" class="circular-timer-container"><script>new CircularTimer("circular-timer-1559", 600)</script></li><li onclick="handleItemClick()">Refrigerate for at least 24 hours. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1560\')" class="alarm-clock-icon"><div id="circular-timer-1560" class="circular-timer-container"><script>new CircularTimer("circular-timer-1560", 86400)</script></li><li onclick="handleItemClick()"></li></ul>' ] );
