ingredients_array.push( [ 'CostanzoRecipes_Entrees_LentilSoup', '<p><ul><li onclick="handleItemClick()">~2 tablespoons oil </li><li onclick="handleItemClick()">1 medium onion, sliced </li><li onclick="handleItemClick()">1 large carrot, sliced (about ¾ cup) </li><li onclick="handleItemClick()">1 clove garlic, finely chopped </li><li onclick="handleItemClick()">4 cups water </li><li onclick="handleItemClick()">12 ounces dried lentils (about 2 cups) </li><li onclick="handleItemClick()">1 bouillon cube </li><li onclick="handleItemClick()">2 tablespoons snipped parsley </li><li onclick="handleItemClick()">1 tablespoon salt </li><li onclick="handleItemClick()">½ teaspoon peppe </li><li onclick="handleItemClick()">¼ to ½ teaspoon dried thyme leaves </li><li onclick="handleItemClick()">1 bay leaf </li><li onclick="handleItemClick()">1 can (28 ounces) whole tomatoes </li><li onclick="handleItemClick()">1 cup water </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Entrees_LentilSoup', '<p><ol><li onclick="handleItemClick()">Heat a large soup pot over medium heat. </li><li onclick="handleItemClick()">Add oil. </li><li onclick="handleItemClick()">Add onion, carrot, celery and garlic; cook, stirring until celery is tender, about 10 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1516\')" class="alarm-clock-icon"><div id="circular-timer-1516" class="circular-timer-container"><script>new CircularTimer("circular-timer-1516", 600)</script></li><li onclick="handleItemClick()">Stir in 4 cups water, the lentils, bouillon cube, parsley, salt, pepper, thyme and bay leaf. </li><li onclick="handleItemClick()">Heat to boiling; reduce heat. </li><li onclick="handleItemClick()">Cover and simmer until soup thickens, about 1 hour <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1517\')" class="alarm-clock-icon"><div id="circular-timer-1517" class="circular-timer-container"><script>new CircularTimer("circular-timer-1517", 3600)</script></li><li onclick="handleItemClick()">Stir in tomatoes with liquid and 1 cup water. </li><li onclick="handleItemClick()">Simmer uncovered 15 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1518\')" class="alarm-clock-icon"><div id="circular-timer-1518" class="circular-timer-container"><script>new CircularTimer("circular-timer-1518", 900)</script></li></ol>' ] );
