ingredients_array.push( [ 'CostanzoRecipes_Entrees_LemonandParmesanCapellini', '<p><ul><li onclick="handleItemClick()">8 oz capellini or similar pasta </li><li onclick="handleItemClick()">1 cup half and half or light cream </li><li onclick="handleItemClick()">3 oz parmesan (or similar) cheese </li><li onclick="handleItemClick()">Finely grated zest of 1 lemon </li><li onclick="handleItemClick()">Salt and freshly ground pepper </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Entrees_LemonandParmesanCapellini', '<p><ol><li onclick="handleItemClick()">Cook pasta until just tender.  </li><li onclick="handleItemClick()">Pour cream into a small pan and bring to boil. </li><li onclick="handleItemClick()">Stir in cheese and lemon rind. </li><li onclick="handleItemClick()">The sauce should thicken in about 30 seconds. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1515\')" class="alarm-clock-icon"><div id="circular-timer-1515" class="circular-timer-container"><script>new CircularTimer("circular-timer-1515", 30)</script></li><li onclick="handleItemClick()">Drain pasta, return to pan, and toss with sauce. </li><li onclick="handleItemClick()">Top with fresh parsley and grated lemon rind if desired. </li></ol>' ] );
