ingredients_array.push( [ 'CostanzoRecipes_Entrees_KedgereewithGreenBeansandMushrooms', '<p><ul><li onclick="handleItemClick()">¾ cup basmati rice </li><li onclick="handleItemClick()">1¼ cups cold water </li><li onclick="handleItemClick()">3 eggs </li><li onclick="handleItemClick()">6 oz green beans, trimmed </li><li onclick="handleItemClick()">¼ cup butter </li><li onclick="handleItemClick()">1 onion, finely chopped </li><li onclick="handleItemClick()">8 oz crimini mushrooms, quartered </li><li onclick="handleItemClick()">2 tbsp light cream or half-and-half </li><li onclick="handleItemClick()">1 tbsp chopped fresh parsley </li><li onclick="handleItemClick()">salt and freshly ground black pepper </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Entrees_KedgereewithGreenBeansandMushrooms', '<p><ol><li onclick="handleItemClick()">Wash the rice several times under cold running water. Drain thoroughly. Bring a pan of water to a boil. Add the rice and cook for 10-12 minutes until tender. Drain thoroughly.. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1510\')" class="alarm-clock-icon"><div id="circular-timer-1510" class="circular-timer-container"><script>new CircularTimer("circular-timer-1510", 600)</script><script>new CircularTimer("circular-timer-1510", 720)</script></li><li onclick="handleItemClick()">Half fill a second pan with water. Add the eggs and bring to a boil. Lower the heat and simmer for 8 minutes. Drain the eggs and cool them under cold water. Remove the shells. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1511\')" class="alarm-clock-icon"><div id="circular-timer-1511" class="circular-timer-container"><script>new CircularTimer("circular-timer-1511", 480)</script></li><li onclick="handleItemClick()">Bring another pan of water to a boil and cook the green beans for 5 minutes. Drain and refresh under cold running water. Then drain again. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1512\')" class="alarm-clock-icon"><div id="circular-timer-1512" class="circular-timer-container"><script>new CircularTimer("circular-timer-1512", 300)</script></li><li onclick="handleItemClick()">Melt the butter in a large frying pan. Add the onions and mushrooms. Cook for 2-3 minutes over a moderate heat. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1513\')" class="alarm-clock-icon"><div id="circular-timer-1513" class="circular-timer-container"><script>new CircularTimer("circular-timer-1513", 120)</script><script>new CircularTimer("circular-timer-1513", 180)</script></li><li onclick="handleItemClick()">Add the green beans and rice to the onion mixture. Stir lightly to mix. Cook for 2 minutes. Cut the hard-boiled eggs in wedges and add  them tto the pan. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1514\')" class="alarm-clock-icon"><div id="circular-timer-1514" class="circular-timer-container"><script>new CircularTimer("circular-timer-1514", 120)</script></li><li onclick="handleItemClick()">Stir in the cream and parsley, taking care noot to break up the eggs. Reheat the kedgeree but do not allow it to boil. Serve at once. </li></ol>' ] );
credits_array.push( [ 'CostanzoRecipes_Entrees_KedgereewithGreenBeansandMushrooms', '<p><ul><li onclick="handleItemClick()">From <a href="https://www.amazon.com/Fast-Vegetarian-Matthew-Drennan-2001-07-31/dp/B01K93C14U/ref=sr_1_1?dchild=1&keywords=Fast+Vegetarian+Food+drennan&qid=1595522860&sr=8-1" target="_blank">Fast Vegetarian Food</a>, by Matthew Drennan, page 92 </li><li onclick="handleItemClick()">date created: [2020-07-23 Thu]  </li></ul>' ] );
ingredients_array.push( [ 'CostanzoRecipes_Entrees_HoppinJohn', '<p><ul><li onclick="handleItemClick()">2 tablespoons extra-virgin olive oil </li><li onclick="handleItemClick()">1 large green bell pepper diced </li><li onclick="handleItemClick()">1 large red bell pepper diced </li><li onclick="handleItemClick()">3 stalks celery </li><li onclick="handleItemClick()">4 medium carrots peeled and diced (about 10 ounces) </li><li onclick="handleItemClick()">3 large cloves garlic minced (about 1 tablespoon) </li><li onclick="handleItemClick()">2 teaspoons smoked paprika </li><li onclick="handleItemClick()">1½  teaspoons chili powder </li><li onclick="handleItemClick()">1¼  teaspoons kosher salt </li><li onclick="handleItemClick()">½ teaspoon cayenne pepper plus additional to taste </li><li onclick="handleItemClick()">1 small bunch kale trimmed and chopped (about 8 ounces) </li><li onclick="handleItemClick()">2 cans (15-ounces) black-eyed peas about 3½  cups, drained and rinsed </li><li onclick="handleItemClick()">1 can (14-ounces) fire-roasted diced tomatoes </li><li onclick="handleItemClick()">Prepared brown rice for serving </li><li onclick="handleItemClick()">Chopped green onions optional for serving </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Entrees_HoppinJohn', '<p><ol><li onclick="handleItemClick()">Heat the oil in a large, deep skillet or Dutch oven over medium heat. </li><li onclick="handleItemClick()">Add the red and green bell peppers, celery, carrots, and garlic and cook, stirring occasionally, until beginning to brown, about 8 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1508\')" class="alarm-clock-icon"><div id="circular-timer-1508" class="circular-timer-container"><script>new CircularTimer("circular-timer-1508", 480)</script></li><li onclick="handleItemClick()">Stir in the smoked paprika, chili powder, salt, and cayenne. Cook, stirring, for 30 seconds. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1509\')" class="alarm-clock-icon"><div id="circular-timer-1509" class="circular-timer-container"><script>new CircularTimer("circular-timer-1509", 30)</script></li><li onclick="handleItemClick()">Working in small handfuls, stir in the kale, stirring after each addition to let it wilt slightly before adding another handful. Cook and stir until you can fit all of the kale in the pot. </li><li onclick="handleItemClick()">Add the black-eyed peas and tomatoes with their juices. Stir and continue to cook until heated through, about 2 additional minutes. </li><li onclick="handleItemClick()">Taste and add additional salt or spices as desired. </li><li onclick="handleItemClick()">Serve hot with rice and a sprinkle of green onion. </li></ol>' ] );
credits_array.push( [ 'CostanzoRecipes_Entrees_HoppinJohn', '<p><ul><li onclick="handleItemClick()">From MaryAnn </li><li onclick="handleItemClick()">date created: [2021-01-02 Sat]  </li><li onclick="handleItemClick()">[2021-01-01 Fri] MaryAnn made some for our New Year\'s Day lunch. Yum! </li><li onclick="handleItemClick()">[2023-01-02 Mon] Made for a bunch of COVID-19 patients: Westfalls, Heimburgs and us(!). </li></ul>' ] );
ingredients_array.push( [ 'CostanzoRecipes_Entrees_GrilledVegetableGratin', '<p><ul><li onclick="handleItemClick()">2 large eggplants (2 pounds) </li><li onclick="handleItemClick()">2 large bell peppers </li><li onclick="handleItemClick()">olive oil </li><li onclick="handleItemClick()">2 large red onions, peeled and sliced </li><li onclick="handleItemClick()">3 cloves garlic, chopped </li><li onclick="handleItemClick()">2 T olive oil </li><li onclick="handleItemClick()">4 c tomatoes, peeled and chopped </li><li onclick="handleItemClick()">3 T fresh basil, chopped </li><li onclick="handleItemClick()">3 T parsley, chopped </li><li onclick="handleItemClick()">1-2 t fresh oregano, chopped </li><li onclick="handleItemClick()">salt and pepper </li><li onclick="handleItemClick()">½ c dry red wine </li><li onclick="handleItemClick()">1 pound rigatoni </li><li onclick="handleItemClick()">½ pound mozzarella, grated </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Entrees_GrilledVegetableGratin', '<p><ol><li onclick="handleItemClick()">Slice the eggplants lengthwise, into  ½-in slices. Salt each slice on both sides and let sit for 30-45 minutes, until sweating a tan-colored water. Rinse each slice for just a second and pat dry.  <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1505\')" class="alarm-clock-icon"><div id="circular-timer-1505" class="circular-timer-container"><script>new CircularTimer("circular-timer-1505", 1800)</script><script>new CircularTimer("circular-timer-1505", 2700)</script></li><li onclick="handleItemClick()">Quarter the peppers lengthwise and remove seeds and stems. </li><li onclick="handleItemClick()">Brush eggplants and peppers with olive oil and grill over charcoal: eggplant, until tender, peppers (skin side down), until charred. Put the peppers in paper bag for a few minutes, then remove the skins. Cut eggplant and peppers into wide strips and set aside. </li><li onclick="handleItemClick()">Heat 2 T olive oil, then saute onion and garlic until golden. Add 3 c tomatoes, the herbs, salt and pepper, and the wine. Simmer until thickened, about 30 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1506\')" class="alarm-clock-icon"><div id="circular-timer-1506" class="circular-timer-container"><script>new CircularTimer("circular-timer-1506", 1800)</script></li><li onclick="handleItemClick()">Preheat oven to 350 degrees and oil the gratin dish. </li><li onclick="handleItemClick()">Cook the rigatoni until just al dente. Drain and mix with tomato sauce and grilled vegetables. </li><li onclick="handleItemClick()">Pour into gratin dish, sprinkle with remaining tomatoes and mozzarella cheese. </li><li onclick="handleItemClick()">Bake until very hot and cheese is beginning to color, about 30-40 minutes. If you like a brown, bubbly gratin topping, place under the broiler for a minute. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1507\')" class="alarm-clock-icon"><div id="circular-timer-1507" class="circular-timer-container"><script>new CircularTimer("circular-timer-1507", 1800)</script><script>new CircularTimer("circular-timer-1507", 2400)</script></li><li onclick="handleItemClick()">Serve immediately. </li></ol>' ] );
credits_array.push( [ 'CostanzoRecipes_Entrees_GrilledVegetableGratin', '<p><ul><li onclick="handleItemClick()">date created: [2010-09-05 Sun]  </li></ul>' ] );
ingredients_array.push( [ 'CostanzoRecipes_Entrees_GrilledCornandSweetOnionSalad', '<p><ul><li onclick="handleItemClick()">12 ears of corn, husks and silk removed </li><li onclick="handleItemClick()">¼ cup aged sherry vinegar </li><li onclick="handleItemClick()">1 tablespoon Dijon mustard </li><li onclick="handleItemClick()">1 garlic clove, minced </li><li onclick="handleItemClick()">½ cup olive oil, plus extra for brushing the corn </li><li onclick="handleItemClick()">2 large red bell peppers, diced </li><li onclick="handleItemClick()">1 large Vidalia onion, thinly sliced (or use a Maui or Walla Walla onion) </li><li onclick="handleItemClick()">4 ripe plum tomatoes, finely chopped </li><li onclick="handleItemClick()">1 jalapeno pepper, finely chopped </li><li onclick="handleItemClick()">¼ cup finely chopped chives </li><li onclick="handleItemClick()">kosher salt and freshly ground pepper </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Entrees_GrilledCornandSweetOnionSalad', '<p><ol><li onclick="handleItemClick()">Preheat a gas or charcoal grill to medium. </li><li onclick="handleItemClick()">In a large pot of boiling water, over high heat, cook the corn 5 minutes until barely tender. Brush with olive oil and grill until slightly charred on all sides, above 2 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1504\')" class="alarm-clock-icon"><div id="circular-timer-1504" class="circular-timer-container"><script>new CircularTimer("circular-timer-1504", 300)</script><script>new CircularTimer("circular-timer-1504", 120)</script></li><li onclick="handleItemClick()">Remove from the grill and with a sharp knife, scrape the kernels into a medium bowl, or stand each ear on a cutting surface and scrape downward to remove the kernels. </li><li onclick="handleItemClick()">Combine the vinegar, mustard and garlic in a large bowl and slowly whisk in the remaining ½ cup olive oil until emulsified. </li><li onclick="handleItemClick()">Add the corn kernels, bell peppers, onion and tomatoes, jalapeno and chives. </li><li onclick="handleItemClick()">Toss to coat with the dressing. </li><li onclick="handleItemClick()">Season with salt and pepper. </li></ol>' ] );
