ingredients_array.push( [ 'CostanzoRecipes_Entrees_GeneralTsosCauliflower', '<p>Prep time 15 mins Cook time 15 mins Total time 30 mins <p>Serves: 6-8 servings <h3 onclick="toggleSection(\'1191\')">for the General Tso’s sauce:<span id="heading-ellipses-1191"></h3><div id=\'heading-content-1191\'><p><ul><li onclick="handleItemClick()">1 tablespoon sesame oil </li><li onclick="handleItemClick()">3 cloves garlic, minced </li><li onclick="handleItemClick()">1 knob of ginger, grated </li><li onclick="handleItemClick()">½ cup chicken or vegetable broth </li><li onclick="handleItemClick()">½ cup soy sauce </li><li onclick="handleItemClick()">⅓ cup rice vinegar </li><li onclick="handleItemClick()">¼ cup sugar (more to taste) </li><li onclick="handleItemClick()">2 tablespoons tomato paste </li><li onclick="handleItemClick()">2 tablespoons cornstarch, dissolved in 1-2 tablespoons of cold water </li></ul></div>  <!-- <h3> --><h3 onclick="toggleSection(\'1192\')">for the Batter:<span id="heading-ellipses-1192"></h3><div id=\'heading-content-1192\'><p><ul><li onclick="handleItemClick()">1 cup flour </li><li onclick="handleItemClick()">⅔ cup cornstarch </li><li onclick="handleItemClick()">1½ teaspoons baking powder </li><li onclick="handleItemClick()">2 teaspoons salt </li><li onclick="handleItemClick()">4 eggs </li><li onclick="handleItemClick()">water as needed (about ½ cup) </li></ul></div>  <!-- <h3> --><h3 onclick="toggleSection(\'1193\')">other ingredients:<span id="heading-ellipses-1193"></h3><div id=\'heading-content-1193\'><p><ul><li onclick="handleItemClick()">1 head cauliflower, cut into small florets </li><li onclick="handleItemClick()">a deep layer of oil for frying </li><li onclick="handleItemClick()">scallions and sesame seeds for topping </li></ul>' ] );
ingredients_array.push( [ 'CostanzoRecipes_Entrees_GarlickyKaleSaladwithCrispyChickpeas', '<p><ul><li onclick="handleItemClick()">1 large bunch of kale </li></ul><h3 onclick="toggleSection(\'1189\')">Chickpeas<span id="heading-ellipses-1189"></h3><div id=\'heading-content-1189\'><p><ul><li onclick="handleItemClick()">1 (15-ounce) can chickpeas, rinsed, drained and thoroughly dried </li><li onclick="handleItemClick()">1½ tablespoon (avocado) oil </li><li onclick="handleItemClick()">1 healthy pinch of sea salt </li><li onclick="handleItemClick()">2½ to 3 tablespoons tandoori masala spice blend </li></ul></div>  <!-- <h3> --><h3 onclick="toggleSection(\'1190\')">Dressing<span id="heading-ellipses-1190"></h3><div id=\'heading-content-1190\'><p><ul><li onclick="handleItemClick()">3 cloves garlic </li><li onclick="handleItemClick()">⅓ cup tahini </li><li onclick="handleItemClick()">2 tablespoons olive oil + more for roasting garlic </li><li onclick="handleItemClick()">¼ cup lemon juice, plus more for massaging kale (about two small lemons) </li><li onclick="handleItemClick()">1 healthy pinch of each salt and pepper </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Entrees_GarlickyKaleSaladwithCrispyChickpeas', '<p><ol><li onclick="handleItemClick()">Preheat oven to 375° F. </li><li onclick="handleItemClick()">Add drained chickpeas to a mixing bowl and toss with oil, salt and tandoori masala. </li><li onclick="handleItemClick()">Pull apart garlic cloves but leave the skin on. (Avoid using any tiny garlic cloves from the center of the head.) </li><li onclick="handleItemClick()">Add garlic cloves and seasoned chickpeas to a baking sheet. </li><li onclick="handleItemClick()">Drizzle garlic with a bit of olive or grapeseed oil. </li><li onclick="handleItemClick()">Bake for 15-20 minutes or until garlic is fragrant and slightly browned. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1501\')" class="alarm-clock-icon"><div id="circular-timer-1501" class="circular-timer-container"><script>new CircularTimer("circular-timer-1501", 900)</script><script>new CircularTimer("circular-timer-1501", 1200)</script></li><li onclick="handleItemClick()">Carefully remove garlic and set aside. </li><li onclick="handleItemClick()">Continue baking chickpeas for 10-15 minutes more or until slightly crispy and golden brown. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1502\')" class="alarm-clock-icon"><div id="circular-timer-1502" class="circular-timer-container"><script>new CircularTimer("circular-timer-1502", 600)</script><script>new CircularTimer("circular-timer-1502", 900)</script></li><li onclick="handleItemClick()">Remove from the oven and set aside (chickpeas will get more crispy as they cool). </li><li onclick="handleItemClick()">Squeeze garlic out of skins (or peel away skins) and add to a blender. </li><li onclick="handleItemClick()">Add all remaining dressing ingredients and blend until smooth. <div class="tip">    If you don\'t have a blender, add the dressing ingredients to a bowl and whisk vigorously to combine, smashing the garlic with the whisk. </div></li><li onclick="handleItemClick()">Taste and adjust seasonings as desired, adding more lemon for brightness, tahini for richness and maple syrup for sweetness. </li><li onclick="handleItemClick()">Set dressing aside. </li><li onclick="handleItemClick()">Add kale to a large mixing bowl. </li><li onclick="handleItemClick()">Before adding the dressing, add 1 tablespoon lemon juice to the kale and massage with hands to soften the texture and lessen the bitterness. </li><li onclick="handleItemClick()">Then add as much dressing as desired (some may be leftover) and mix with a spoon or salad tongs. </li><li onclick="handleItemClick()">Top with chickpeas and serve. <div class="tip">    Best when fresh, although leftover keep in the fridge for up to a few days. </div></li></ol>' ] );
