ingredients_array.push( [ 'CostanzoRecipes_Entrees_GarlicWhitePizzaSauce', '<p><ul><li onclick="handleItemClick()">2 tablespoons olive oil </li><li onclick="handleItemClick()">4 cloves garlic, minced </li><li onclick="handleItemClick()">1/4 teaspoon dried oregano </li><li onclick="handleItemClick()">1/4 teaspoon ground black pepper </li><li onclick="handleItemClick()">1/4 teaspoon salt </li><li onclick="handleItemClick()">2-3 tablespoons flour </li><li onclick="handleItemClick()">1 1/2 cups half & half milk works fine, too </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Entrees_GarlicWhitePizzaSauce', '<p><ol><li onclick="handleItemClick()">In a skillet on medium-high heat, heat oil. </li><li onclick="handleItemClick()">Add the garlic and heat for about 3 minutes, until fragrant. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1498\')" class="alarm-clock-icon"><div id="circular-timer-1498" class="circular-timer-container"><script>new CircularTimer("circular-timer-1498", 180)</script></li><li onclick="handleItemClick()">Add the flour (start with 2 tablespoons), oregano, pepper, and salt; whisk well. </li><li onclick="handleItemClick()">Add the half & half all at once, whisking constantly; cook and stir until bubbly and until thickened (usually about 3 minutes). <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1499\')" class="alarm-clock-icon"><div id="circular-timer-1499" class="circular-timer-container"><script>new CircularTimer("circular-timer-1499", 180)</script></li></ol><p><div class="tip">If you want the sauce a bit thicker, mix your remaining 1 tablespoon of flour with a bit of milk until you have a mashed potato consistency. Pour that mixture into the bubbling sauce and whisk constantly. Cook about 1 minute more. It should thicken the sauce. Repeat until you have it the thickness you want. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1500\')" class="alarm-clock-icon"><div id="circular-timer-1500" class="circular-timer-container"><script>new CircularTimer("circular-timer-1500", 60)</script></div>' ] );
credits_array.push( [ 'CostanzoRecipes_Entrees_GarlicWhitePizzaSauce', '<p>Favorite <p><ul><li onclick="handleItemClick()">From <a href="https://www.nelliebellie.com/wprm_print/42899" target="_blank">https://www.nelliebellie.com/wprm_print/42899</a> </li><li onclick="handleItemClick()">date created: [2022-05-23 Mon]  </li><li onclick="handleItemClick()">[2022-05-21 Sat] Made for T, G&MA and Peggy and Doug. Very flavorful pizza base. Reminded us of a ricotta sauce. </li></ul>' ] );
ingredients_array.push( [ 'CostanzoRecipes_Entrees_EasyCoconutCurrySauce', '<p><ul><li onclick="handleItemClick()">2 tbsp vegetable oil </li><li onclick="handleItemClick()">4 garlic cloves , minced </li><li onclick="handleItemClick()">2 tsp fresh ginger , finely grated </li><li onclick="handleItemClick()">1/2 onion , finely chopped (brown or yellow) </li><li onclick="handleItemClick()">400g/14oz can coconut milk (full fat) </li><li onclick="handleItemClick()">1 cup/ 250 ml chicken broth </li><li onclick="handleItemClick()">1/2 cup / 125 ml tomato passata (tomato puree) (Note 1) </li><li onclick="handleItemClick()">400 g/14 oz can brown lentils , drained </li><li onclick="handleItemClick()">250 g / 8 oz pumpkin (~3 cups) , cut into 2cm / 4/5" cubes </li><li onclick="handleItemClick()">80 g / 2 cups (packed) baby spinach </li><li onclick="handleItemClick()">1/2 cup cashews , unsalted (roasted or raw) (Note 2) </li><li onclick="handleItemClick()">1/2 tsp each salt + pepper </li></ul><h3 onclick="toggleSection(\'1188\')">Spices:<span id="heading-ellipses-1188"></h3><div id=\'heading-content-1188\'><p><ul><li onclick="handleItemClick()">1 tbsp garam masala (Note 3) </li><li onclick="handleItemClick()">2 tbsp curry powder (mild or spicy, your choice) (Note 4) </li><li onclick="handleItemClick()">2 tsp cumin </li><li onclick="handleItemClick()">1 tsp coriander powder </li><li onclick="handleItemClick()">1 tsp paprika (sweet, or hot for spice!) </li><li onclick="handleItemClick()">1/2 tsp turmeric </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Entrees_EasyCoconutCurrySauce', '<p><ol><li onclick="handleItemClick()">Heat oil in a deep skillet or pot over medium high heat. Add onion, garlic and ginger, cook for 2 minutes until onion is a bit tinged with gold. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1494\')" class="alarm-clock-icon"><div id="circular-timer-1494" class="circular-timer-container"><script>new CircularTimer("circular-timer-1494", 120)</script></li><li onclick="handleItemClick()">Add Spices and stir for 1 minute - don\'t worry if it looks a bit dried out. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1495\')" class="alarm-clock-icon"><div id="circular-timer-1495" class="circular-timer-container"><script>new CircularTimer("circular-timer-1495", 60)</script></li><li onclick="handleItemClick()">Add coconut milk, passata and broth. Stir until incorporated. </li><li onclick="handleItemClick()">Add lentils and pumpkin. Bring to simmer then adjust heat so it\'s simmering energetically. </li><li onclick="handleItemClick()">Simmer for 10 minutes or until pumpkin is tender but not mushy, and sauce has thickened. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1496\')" class="alarm-clock-icon"><div id="circular-timer-1496" class="circular-timer-container"><script>new CircularTimer("circular-timer-1496", 600)</script></li><li onclick="handleItemClick()">Stir through baby spinach until wilted, then cashew nuts. </li><li onclick="handleItemClick()">Add salt and pepper last, adjusting to taste. </li><li onclick="handleItemClick()">Serve over rice, sprinkled with coriander and extra cashews, if using. </li></ol><p><div class="note"><ol><li onclick="handleItemClick()">Crushed canned tomato can also be used here but doesn\'t yield the same smooth sauce. Use 3/4 cup. </li><li onclick="handleItemClick()">Roughly chopped macadamia or pistachios would be a great sub. Otherwise, leave the cashews out and sprinkle generously with store bought fried shallots or onions (Asian section of supermarket), these are a great alternative for providing texture and added interest into the curry. </li><li onclick="handleItemClick()">Garam Masala is a spice mix that is found in major supermarkets in Australia (Woolies, Coles etc) that is commonly used in Indian cooking. It smells like curry powder and is not spicy, if you buy it from standard supermarkets. If you get it from a speciality store, check the spiciness before using - you may wish to dial it back a bit. </li><li onclick="handleItemClick()">I use Cloves of India (sold at supermarkets, spice section), mild or spicy. Any curry powder will be fine here because it\'s not intended to be a full blown authentic Indian curry. </li><li onclick="handleItemClick()">The Formula: This recipe is all about the curry sauce, so feel free to use it for whatever you want to put in it. It makes about 3 1/2 cups of sauce which is enough for about 4 - 5 packed cups of "stuff".    Examples: 500g/1 lb chicken cut into bite size pieces (~2 1/2 cups) + 2 1/2 cups of chopped veggies of choice. Or 500g / 1 lb prawns / shrimp + 2 cups chopped sweet potato. Or 5 cups of veggies, chopped into bite size pieces. Or 1 can of beans (~2 cups) plus 3 cups of chopped veggies. Etc etc. 🙂    Stir throughs like spinach which wilts down to nothing is a freebie that doesn\'t count in the cups allowance, and similarly with a small amount of nuts.    Proteins (chicken, turkey, beef, lamb, fish and prawns/shrimp especially great) - brown first then remove (still raw inside ok), then proceed with recipe. Add it back in to finish cooking through / reheat. For beef and lamb, be sure to use a nice marbled cut and don\'t overcook, keep it juicy inside. You could also stir through shredded or chopped precooked meat.    Vegetables, add them into the sauce based on the cook time. The sauce needs 10 minutes to reduce / flavours to develop.    Legumes (beans) - whether canned or cooked dried beans, add them in as soon as the sauce is mixed and simmer for the full time so they soak up some flavour.    Dried lentils (2/3 dried = 1 can drained) - use 2/3 cup dried and simmer in 3 cups of lightly salted water for  20 minutes or until tender but still firm (not mushy). Drain and use per recipe. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1497\')" class="alarm-clock-icon"><div id="circular-timer-1497" class="circular-timer-container"><script>new CircularTimer("circular-timer-1497", 600)</script><script>new CircularTimer("circular-timer-1497", 1200)</script></li></ol></div>' ] );
credits_array.push( [ 'CostanzoRecipes_Entrees_EasyCoconutCurrySauce', '<p><ul><li onclick="handleItemClick()">From <a href="https://www.recipetineats.com/easy-coconut-curry/" target="_blank">https://www.recipetineats.com/easy-coconut-curry/</a> </li><li onclick="handleItemClick()">date created: [2023-01-07 Sat]  </li></ul>' ] );
ingredients_array.push( [ 'CostanzoRecipes_Entrees_DriedMushroomSoupwithBarley', '<p><ul><li onclick="handleItemClick()">½ ounce dried mushrooms, such as porcini, shiitake, chanterelle and/or morel -- about ⅔ cup </li><li onclick="handleItemClick()">6 cups boiling water </li><li onclick="handleItemClick()">1 tsp canola or other vegetable oil </li><li onclick="handleItemClick()">2 cups chopped onions </li><li onclick="handleItemClick()">2 cloves garlic, pressed </li><li onclick="handleItemClick()">1 cup finely chopped celery </li><li onclick="handleItemClick()">1 cup carrots, peeled and finely chopped </li><li onclick="handleItemClick()">3 cups sliced fresh mushrooms (about 10 oz.) </li><li onclick="handleItemClick()">¼ cup soy sauce </li><li onclick="handleItemClick()">pinch dried thyme </li><li onclick="handleItemClick()">¼ tsp ground black pepper </li><li onclick="handleItemClick()">½ cup raw pearl barley </li><li onclick="handleItemClick()">¼ cup dry shery or 2 tsp honey </li><li onclick="handleItemClick()">salt and ground black pepper to taste </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Entrees_DriedMushroomSoupwithBarley', '<p><ol><li onclick="handleItemClick()">In a saucepan, cover the dried mushrooms with 6 cups boiling water. Simmer for 15 minutes. Remove from heat and set aside for at least 15 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1491\')" class="alarm-clock-icon"><div id="circular-timer-1491" class="circular-timer-container"><script>new CircularTimer("circular-timer-1491", 900)</script><script>new CircularTimer("circular-timer-1491", 900)</script></li><li onclick="handleItemClick()">Meanwhile, warm the oil in a soup pot on low heat. Add the onions and garlic, cover and cook for 5 minutes, stirring occasionally. Add the celery and carrots and cook for 5 minutes, stirring occasionally. Add the fresh mushrooms, increase the heat to medium and cook, stirring continuously, for about 3 minutes, until the mushrooms begin to release their jucies. Remove from the heat and set aside. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1492\')" class="alarm-clock-icon"><div id="circular-timer-1492" class="circular-timer-container"><script>new CircularTimer("circular-timer-1492", 300)</script><script>new CircularTimer("circular-timer-1492", 300)</script><script>new CircularTimer("circular-timer-1492", 180)</script></li><li onclick="handleItemClick()">Drain the dried mushrooms, reserving the mushroom stock. Cut off and discard any tough stems, then rinse and chop the mushrooms. Strain the stock through a sieve or-- if dirt or sand is evident-- through a paper coffee filter. Add enough water to the stock to make 7 cups of liquid.  </li><li onclick="handleItemClick()">Add the chopped dried mushrooms, mushroom stock, soy sauce, thyme, pepper, barley and optional sherry or honey to the pot of sauteed vegetables. Bring to a boil and then cover; reduce the heat and simmer for 45 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1493\')" class="alarm-clock-icon"><div id="circular-timer-1493" class="circular-timer-container"><script>new CircularTimer("circular-timer-1493", 2700)</script></li><li onclick="handleItemClick()">When ready to serve, add salt and pepper to taste. The soup should be brothy, so if it has cooked down and become thick, add a bit more water. </li></ol>' ] );
credits_array.push( [ 'CostanzoRecipes_Entrees_DriedMushroomSoupwithBarley', '<p><ul><li onclick="handleItemClick()">Per 8 oz serving: 94 calories, 3.5 g protein, 1.2 g fat, 19 g carbs, 4.3 g dietary fiber </li><li onclick="handleItemClick()">[As served during Easter weeken at Tracey and John\'s; April 2004; Recipe Source: Moosewood Restaurant Low-Fat Favorites, page 94] </li><li onclick="handleItemClick()">date created: [2007-06-27 Wed]  </li></ul>' ] );
ingredients_array.push( [ 'CostanzoRecipes_Entrees_Dhall', '<p><ul><li onclick="handleItemClick()">1½ cups dried lentils </li><li onclick="handleItemClick()">4½ cups vegetable stock (or water) </li><li onclick="handleItemClick()">1 teaspoon turmeric </li><li onclick="handleItemClick()">1 teaspoon salt </li><li onclick="handleItemClick()">2 tablespoon vegetable oil </li><li onclick="handleItemClick()">2 cups diced onions </li><li onclick="handleItemClick()">1 garlic clove, minced or pressed </li><li onclick="handleItemClick()">½ teaspoon ground cumin </li><li onclick="handleItemClick()">1 teaspoon turmeric </li><li onclick="handleItemClick()">½ teaspoon black pepper, coarsely ground </li><li onclick="handleItemClick()">¼ teaspoon hot chili pepper flakes or 1 small fresh hot chili pepper, minced </li><li onclick="handleItemClick()">1 cup coconut milk </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Entrees_Dhall', '<p><ol><li onclick="handleItemClick()">In a large pot, cook the lentils in the stock or water along with the salt and turmeric. </li><li onclick="handleItemClick()">Drain but reserve the liquid. </li><li onclick="handleItemClick()">Saute the onions, garlic and rest of the spices in the oil until the onions are golden brown, stirring continuously. </li><li onclick="handleItemClick()">Mix the drained lentils with the rest of the spices and reheat gently. </li><li onclick="handleItemClick()">When hot, stir in the coconut milk. </li><li onclick="handleItemClick()">If the dhall is too dry or thick, add some of the reserved liquid. </li><li onclick="handleItemClick()">For a lighter, less sweet version of this dish, substitute 1 cup chopped tomatoes for the coconut milk. </li></ol>' ] );
