ingredients_array.push( [ 'CostanzoRecipes_Entrees_CornMacqueChoux', '<p><ul><li onclick="handleItemClick()">4 tbl. butter </li><li onclick="handleItemClick()">3 ears of corn, shucked and cut off cob </li><li onclick="handleItemClick()">1 cup chopped onion </li><li onclick="handleItemClick()">½ cup diced celery </li><li onclick="handleItemClick()">½ cup diced bell pepper </li><li onclick="handleItemClick()">1 tbl. minced garlic </li><li onclick="handleItemClick()">1 tbl. minced fresh thyme </li><li onclick="handleItemClick()">1 tbl cajun seasoning blend (recipe below) </li><li onclick="handleItemClick()">1-10 oz. can diced Rotel Tomatoes (drained) – these can be found in most grocery stores either in the Mexican section or with other canned tomatoes.  Rotel is a brand name. </li><li onclick="handleItemClick()">1 lb peeled shrimp, lightly sauteed </li><li onclick="handleItemClick()">2 cups heavy cream </li><li onclick="handleItemClick()">¼ cup parmesan, grated </li><li onclick="handleItemClick()">¼ cup green onions, diced </li><li onclick="handleItemClick()">¼ cup parsley, minced </li></ul><h3 onclick="toggleSection(\'1181\')">Pat’s Cajun Seasoning Blend<span id="heading-ellipses-1181"></h3><div id=\'heading-content-1181\'><p><ul><li onclick="handleItemClick()">1½ cups salt </li><li onclick="handleItemClick()">½ cup cayenne pepper </li><li onclick="handleItemClick()">¼ cup paprika </li><li onclick="handleItemClick()">¼ cup granulated onion </li><li onclick="handleItemClick()">¼ cup granulated garlic </li><li onclick="handleItemClick()">2 tbl. freshly ground black pepper </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Entrees_CornMacqueChoux', '<p><ol><li onclick="handleItemClick()">Heat butter in saucepot.  Add corn and cook 10 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1475\')" class="alarm-clock-icon"><div id="circular-timer-1475" class="circular-timer-container"><script>new CircularTimer("circular-timer-1475", 600)</script></li><li onclick="handleItemClick()">Add onion, celery, bell pepper, garlic, and thyme.  Cook for additional 5 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1476\')" class="alarm-clock-icon"><div id="circular-timer-1476" class="circular-timer-container"><script>new CircularTimer("circular-timer-1476", 300)</script></li><li onclick="handleItemClick()">Add cajun seasoning and Rotel tomatoes and cook for 10 minutes until water from tomatoes has evaporated. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1477\')" class="alarm-clock-icon"><div id="circular-timer-1477" class="circular-timer-container"><script>new CircularTimer("circular-timer-1477", 600)</script></li><li onclick="handleItemClick()">Stir in shrimp, heavy cream, and parmesan, bring to a boil; lower fire and simmer for 15-20 minutes until cream thickens slightly. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1478\')" class="alarm-clock-icon"><div id="circular-timer-1478" class="circular-timer-container"><script>new CircularTimer("circular-timer-1478", 900)</script><script>new CircularTimer("circular-timer-1478", 1200)</script></li><li onclick="handleItemClick()">Stir in green onions and parsley.   </li><li onclick="handleItemClick()">Serve or rice, pasta, or polenta. </li></ol>' ] );
