ingredients_array.push( [ 'CostanzoRecipes_Entrees_CornChowder', '<p><ul><li onclick="handleItemClick()">2 onions, chopped </li><li onclick="handleItemClick()">3 potatoes, diced </li><li onclick="handleItemClick()">8 ounces frozen corn </li><li onclick="handleItemClick()">2 cans creamed corn </li><li onclick="handleItemClick()">1 can\'s worth of fresh (or evaporated) milk </li><li onclick="handleItemClick()">2-3 Tblsp butter (optional) </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Entrees_CornChowder', '<p><ol><li onclick="handleItemClick()">In a stick-proof soup pot, cook onion in 1 Tblsp butter until soft. Add potatoes and cover with water. Simmer until potatoes are almost soft. </li><li onclick="handleItemClick()">Add frozen corn and cook for 2 minutes. Add creamed corn; let simmer for 5 minutes. Add 1 can milk and butter (optional). Serve when hot. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1474\')" class="alarm-clock-icon"><div id="circular-timer-1474" class="circular-timer-container"><script>new CircularTimer("circular-timer-1474", 120)</script><script>new CircularTimer("circular-timer-1474", 300)</script></li></ol>' ] );
