ingredients_array.push( [ 'CostanzoRecipes_Entrees_ChipotleLimeCauliflowerTacos', '<p><ul><li onclick="handleItemClick()">¼ cup lime juice (from about 2 limes) </li><li onclick="handleItemClick()">1-2 tablespoons chopped chipotles in adobo sauce (see Tips) </li><li onclick="handleItemClick()">1 tablespoon honey </li><li onclick="handleItemClick()">2 cloves garlic </li><li onclick="handleItemClick()">½ teaspoon salt </li><li onclick="handleItemClick()">1 small head cauliflower (see Tips), cut into bite-size pieces </li><li onclick="handleItemClick()">1 small red onion, halved and thinly sliced </li><li onclick="handleItemClick()">1 (15 ounce) can refried black beans, warmed </li><li onclick="handleItemClick()">8 corn tortillas, warmed </li><li onclick="handleItemClick()">½ cup crumbled queso fresco or feta cheese </li><li onclick="handleItemClick()">Sliced red cabbage, fresh cilantro, guacamole, jalapeño slices and/or lime wedges for serving </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Entrees_ChipotleLimeCauliflowerTacos', '<p><ol><li onclick="handleItemClick()">Preheat oven to 450 degrees F. Line a large rimmed baking sheet with foil. </li><li onclick="handleItemClick()">Combine lime juice, chipotles to taste, honey, garlic and salt in a blender; process until mostly smooth. </li><li onclick="handleItemClick()">Place cauliflower in a large bowl, add the sauce and stir to coat. </li><li onclick="handleItemClick()">Transfer to the prepared baking sheet. </li><li onclick="handleItemClick()">Sprinkle onion on top. </li><li onclick="handleItemClick()">Roast, stirring once, until the cauliflower is tender and browned in spots, 18 to 20 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1473\')" class="alarm-clock-icon"><div id="circular-timer-1473" class="circular-timer-container"><script>new CircularTimer("circular-timer-1473", 1080)</script><script>new CircularTimer("circular-timer-1473", 1200)</script></li><li onclick="handleItemClick()">Serve the vegetables and beans in tortillas, topped with cheese and garnished with cabbage, cilantro, guacamole, jalapeños and/or lime wedges. </li></ol>' ] );
credits_array.push( [ 'CostanzoRecipes_Entrees_ChipotleLimeCauliflowerTacos', '<p><div class="tip">Look for small cans of smoked chipotle peppers in adobo sauce near other Mexican ingredients in well-stocked supermarkets. Once opened, refrigerate for up to 2 weeks or freeze for up to 6 months. </div><p><div class="tip">To prep cauliflower: Remove any outer leaves. Cut off the stem. Turn head upside down and, holding the knife at a 45-degree angle, slice around the stem to remove the core from center of head. Cut the head into large florets. Then, cut or slice florets to desired sizes. </div><p><ul><li onclick="handleItemClick()">From <a href="https://www.eatingwell.com/recipe/257781/chipotle-lime-cauliflower-tacos/" target="_blank">https://www.eatingwell.com/recipe/257781/chipotle-lime-cauliflower-tacos/</a> </li><li onclick="handleItemClick()">date created: [2021-09-27 Mon]  </li></ul>' ] );
ingredients_array.push( [ 'CostanzoRecipes_Entrees_ChickpeaandTatsoiCoconutCurry', '<p><ul><li onclick="handleItemClick()">1 cup basmati rice (or rice of choice) </li><li onclick="handleItemClick()">2 Tablespoons coconut oil </li><li onclick="handleItemClick()">1 medium sized onion, diced </li><li onclick="handleItemClick()">3 cloves of garlic, minced </li><li onclick="handleItemClick()">2 teaspoons curry powder </li><li onclick="handleItemClick()">1/4 teaspoon crushed red pepper flakes </li><li onclick="handleItemClick()">salt and pepper freshly ground black pepper </li><li onclick="handleItemClick()">1/2 cup vegetables stock (or water) </li><li onclick="handleItemClick()">1 (15 ounce) can of chickpeas, drained </li><li onclick="handleItemClick()">1 (15 ounce) can full-fat coconut milk </li><li onclick="handleItemClick()">1 Tablespoon fresh lime juice </li><li onclick="handleItemClick()">3 cups roughly chopped tatsoi (and/or bok choy) </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Entrees_ChickpeaandTatsoiCoconutCurry', '<p><ol><li onclick="handleItemClick()">Cook the rice according to specific instructions on the package. </li><li onclick="handleItemClick()">Heat the oil in a large dutch oven or soup pot over medium heat. </li><li onclick="handleItemClick()">Add the onion and cook, stirring occasionally until fragrant, about 5 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1470\')" class="alarm-clock-icon"><div id="circular-timer-1470" class="circular-timer-container"><script>new CircularTimer("circular-timer-1470", 300)</script></li><li onclick="handleItemClick()">Add the garlic, curry powder, red pepper flakes and a healthy pinch of salt and pepper. </li><li onclick="handleItemClick()">Cook for 1 minute longer, stirring often to coat the onion and garlic in the spices. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1471\')" class="alarm-clock-icon"><div id="circular-timer-1471" class="circular-timer-container"><script>new CircularTimer("circular-timer-1471", 60)</script></li><li onclick="handleItemClick()">Add in the vegetable stock, chickpeas and coconut milk. </li><li onclick="handleItemClick()">Bring to a boil. </li><li onclick="handleItemClick()">Reduce the heat and simmer for 10 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1472\')" class="alarm-clock-icon"><div id="circular-timer-1472" class="circular-timer-container"><script>new CircularTimer("circular-timer-1472", 600)</script></li><li onclick="handleItemClick()">Add the lime juice and tatsoi and keep on low heat until the tatsoi wilts down. </li><li onclick="handleItemClick()">Season to taste with additional salt and pepper. </li><li onclick="handleItemClick()">Serve the soup with a scoop of rice and enjoy. </li></ol>' ] );
credits_array.push( [ 'CostanzoRecipes_Entrees_ChickpeaandTatsoiCoconutCurry', '<p><ul><li onclick="handleItemClick()">From <a href="https://blog.freshharvestga.com/chickpea-tatsoi-coconut-curry/" target="_blank">https://blog.freshharvestga.com/chickpea-tatsoi-coconut-curry/</a> </li><li onclick="handleItemClick()">date created: [2020-11-01 Sun]  </li></ul>' ] );
ingredients_array.push( [ 'CostanzoRecipes_Entrees_CheddarSweetCornPie', '<p><ul><li onclick="handleItemClick()">3 tablespoons unsalted butter, divided (2 tablespoons + 1 tablespoon) </li><li onclick="handleItemClick()">1/2 yellow onion, finely chopped </li><li onclick="handleItemClick()">2 cloves garlic, minced </li><li onclick="handleItemClick()">4 cups fresh or frozen (and thawed) corn kernels </li><li onclick="handleItemClick()">2/3 cup half-and-half </li><li onclick="handleItemClick()">2 cups sharp cheddar cheese, grated, divided (1½ cups + ½ cup) </li><li onclick="handleItemClick()">3/4 cup panko or seasoned breadcrumbs, divided (½ cup + ¼ cup) </li><li onclick="handleItemClick()">3 large eggs </li><li onclick="handleItemClick()">1/2 teaspoon chili powder </li><li onclick="handleItemClick()">1/2 teaspoon paprika </li><li onclick="handleItemClick()">3 1/2 tablespoons fresh chives, plus extra for garnish, finely chopped, optional </li><li onclick="handleItemClick()">kosher salt and freshly ground pepper, to taste </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Entrees_CheddarSweetCornPie', '<p><ol><li onclick="handleItemClick()">Preheat oven to 375º F and lightly grease a pie dish with butter or non-stick spray. </li><li onclick="handleItemClick()">Melt 2 tablespoons butter in a large pan or skillet over medium-high heat and sauté onion until softened and translucent, 6-8 minutes. Season with salt and pepper. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1466\')" class="alarm-clock-icon"><div id="circular-timer-1466" class="circular-timer-container"><script>new CircularTimer("circular-timer-1466", 360)</script><script>new CircularTimer("circular-timer-1466", 480)</script></li><li onclick="handleItemClick()">Add garlic and cook for another 1-2 minutes, or until fragrant, then transfer mixture to a large bowl. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1467\')" class="alarm-clock-icon"><div id="circular-timer-1467" class="circular-timer-container"><script>new CircularTimer("circular-timer-1467", 60)</script><script>new CircularTimer("circular-timer-1467", 120)</script></li><li onclick="handleItemClick()">Add corn, half-and-half, 1½ cups cheddar cheese, ½ cup breadcrumbs, chives, salt and pepper to the bowl with the onion and mix until combined. </li><li onclick="handleItemClick()">In a separate bowl, beat eggs with chili powder and paprika, then stir mixture into bowl with corn and onion until incorporated. Pour mixture into greased pie dish. </li><li onclick="handleItemClick()">Microwave remaining 1 tablespoon butter until melted. Let cool slightly, then whisk together with remaining ¼ cup panko crumbs, ½ cup cheese and chives. Season with salt and pepper. </li><li onclick="handleItemClick()">Evenly sprinkle panko mixture over corn, then place pie dish in oven and bake for 35 minutes, or until corn is puffed up and edges are starting to brown. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1468\')" class="alarm-clock-icon"><div id="circular-timer-1468" class="circular-timer-container"><script>new CircularTimer("circular-timer-1468", 2100)</script></li><li onclick="handleItemClick()">Remove from oven and let cool 5-10 minutes, then top with more chives or paprika. Enjoy! <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1469\')" class="alarm-clock-icon"><div id="circular-timer-1469" class="circular-timer-container"><script>new CircularTimer("circular-timer-1469", 300)</script><script>new CircularTimer("circular-timer-1469", 600)</script></li></ol>' ] );
credits_array.push( [ 'CostanzoRecipes_Entrees_CheddarSweetCornPie', '<p><ul><li onclick="handleItemClick()">From <a href="https://12tomatoes.com/cheddar-corn-pie/" target="_blank">https://12tomatoes.com/cheddar-corn-pie/</a> </li><li onclick="handleItemClick()">date created: [2020-12-05 Sat]  </li></ul>' ] );
ingredients_array.push( [ 'CostanzoRecipes_Entrees_CharlestonGumbo', '<p><ul><li onclick="handleItemClick()">1 quart okra, chopped </li><li onclick="handleItemClick()">1 pint canned tomatoes </li><li onclick="handleItemClick()">½ small onion, chopped </li><li onclick="handleItemClick()">salt, pepper and Worcestershire sauce, to taste  </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Entrees_CharlestonGumbo', '<p><ol><li onclick="handleItemClick()">Combine tomatoes, okra, onion, salt, pepper and Worcestershire. </li><li onclick="handleItemClick()">Let simmer for two hours. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1465\')" class="alarm-clock-icon"><div id="circular-timer-1465" class="circular-timer-container"><script>new CircularTimer("circular-timer-1465", 7200)</script></li></ol><p><div class="note">The original recipe calls for 2 slices of bacon, so it may need a bit of spicing up. </div><p>Becky says: <p><div class="pull-quote">This is one of the most-often cited Southern foods that show strong African influence. Serve over rice. Canteloupe is an amazing go-with. I do not grind the ingredients! </div>' ] );
