ingredients_array.push( [ 'CostanzoRecipes_Entrees_ButternutSquashSoup', '<p><ul><li onclick="handleItemClick()">3 lbs butternut squash </li><li onclick="handleItemClick()">7 Tbsp butter </li><li onclick="handleItemClick()">2 leeks - thinly sliced (onions will do in a pinch!) </li><li onclick="handleItemClick()">1 qt vegetable stock </li><li onclick="handleItemClick()">¼ tsp nutmeg </li><li onclick="handleItemClick()">½ cup peas (frozen or fresh) </li><li onclick="handleItemClick()">8 cups fresh spinach (approx. ½ lbs = 10 oz. bag) </li><li onclick="handleItemClick()">1¼ cup light cream </li><li onclick="handleItemClick()">salt and pepper to taste </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Entrees_ButternutSquashSoup', '<p><ol><li onclick="handleItemClick()">Slice squash length-wise in half. Place in microwave-proof pan with a small amount of water and microwave on high until flesh is tender (perhaps 20 minutes, depending on microwave). <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1455\')" class="alarm-clock-icon"><div id="circular-timer-1455" class="circular-timer-container"><script>new CircularTimer("circular-timer-1455", 1200)</script></li><li onclick="handleItemClick()">Wash and drain spinach, trim stems, chop and set aside. </li><li onclick="handleItemClick()">When squash is cool enough to handle, scoop out. </li><li onclick="handleItemClick()">Melt butter in a stock pot and add leeks. Saute until soft. Add stock, squash, nutmeg, salt and pepper. Bring to a boil.  </li><li onclick="handleItemClick()">Puree soup in a food processor. </li><li onclick="handleItemClick()">Return to pot and add spinach and peas. Simmer until spinach is slightly tender. </li><li onclick="handleItemClick()">Stir in cream, heat and serve. </li></ol>' ] );
credits_array.push( [ 'CostanzoRecipes_Entrees_ButternutSquashSoup', '<p><ul><li onclick="handleItemClick()">[Served Easter weekend, April 2004 at Tracey and John\'s] </li><li onclick="handleItemClick()">date created: [2007-06-27 Wed]  </li></ul>' ] );
ingredients_array.push( [ 'CostanzoRecipes_Entrees_BlackBeanandRiceEnchiladas', '<p><ul><li onclick="handleItemClick()">1 tablespoon olive oil </li><li onclick="handleItemClick()">1 green bell pepper, chopped </li><li onclick="handleItemClick()">1 onion, chopped </li><li onclick="handleItemClick()">3 cloves garlic, minced </li><li onclick="handleItemClick()">1 (15 ounce) can black beans, rinsed and drained </li><li onclick="handleItemClick()">1 (14.5 ounce) can diced tomatoes and green chilies </li><li onclick="handleItemClick()">¼ cup picante sauce </li><li onclick="handleItemClick()">1 tablespoon chili powder </li><li onclick="handleItemClick()">1 teaspoon ground cumin </li><li onclick="handleItemClick()">¼ teaspoon red pepper flakes </li><li onclick="handleItemClick()">2 cups cooked brown rice </li><li onclick="handleItemClick()">8 (6 inch) flour tortillas, warmed </li><li onclick="handleItemClick()">1 cup salsa </li><li onclick="handleItemClick()">1 cup shredded Cheddar cheese </li><li onclick="handleItemClick()">3 tablespoons chopped fresh cilantro leaves </li><li onclick="handleItemClick()">¼ cup shredded Cheddar cheese </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Entrees_BlackBeanandRiceEnchiladas', '<p><ol><li onclick="handleItemClick()">Preheat oven to 350 degrees F (175 degrees C). Lightly grease a 9x13-inch baking dish. </li><li onclick="handleItemClick()">Heat oil in a large skillet over medium heat; cook and stir green pepper, onion, and garlic until tender, about 7 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1450\')" class="alarm-clock-icon"><div id="circular-timer-1450" class="circular-timer-container"><script>new CircularTimer("circular-timer-1450", 420)</script></li><li onclick="handleItemClick()">Stir in beans, tomatoes, picante sauce, chili powder, cumin, and red pepper flakes; bring to a boil. Reduce heat to low and simmer, uncovered, until heated through and mixture thickens, about 5 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1451\')" class="alarm-clock-icon"><div id="circular-timer-1451" class="circular-timer-container"><script>new CircularTimer("circular-timer-1451", 300)</script></li><li onclick="handleItemClick()">Fold in rice and 1 cup Cheddar cheese; cook until heated through, about 5 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1452\')" class="alarm-clock-icon"><div id="circular-timer-1452" class="circular-timer-container"><script>new CircularTimer("circular-timer-1452", 300)</script></li><li onclick="handleItemClick()">Spoon a rounded ½ cup bean mixture down the center of each tortilla. Fold sides over filling and roll up. Place enchiladas seam side down in baking dish; spoon salsa over each tortilla. Cover baking dish with aluminum foil. </li><li onclick="handleItemClick()">Bake in preheated oven for 25 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1453\')" class="alarm-clock-icon"><div id="circular-timer-1453" class="circular-timer-container"><script>new CircularTimer("circular-timer-1453", 1500)</script></li><li onclick="handleItemClick()">Uncover and sprinkle with cilantro and ¼ cup Cheddar cheese. Bake until cheese is melted, 2 to 3 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1454\')" class="alarm-clock-icon"><div id="circular-timer-1454" class="circular-timer-container"><script>new CircularTimer("circular-timer-1454", 120)</script><script>new CircularTimer("circular-timer-1454", 180)</script></li></ol>' ] );
