ingredients_array.push( [ 'CostanzoRecipes_Entrees_BellPepperCasserole', '<p><ul><li onclick="handleItemClick()">½ cup olive oil </li><li onclick="handleItemClick()">1 large onion chopped </li><li onclick="handleItemClick()">1 cup carrots grated </li><li onclick="handleItemClick()">4 bell peppers chopped </li><li onclick="handleItemClick()">5 tomatoes chopped (one large can) </li><li onclick="handleItemClick()">2 cups cooked brown rice </li><li onclick="handleItemClick()">1 cup or can cooked black beans </li><li onclick="handleItemClick()">5 cloves garlic chopped </li><li onclick="handleItemClick()">1 cup panko breadcrumbs </li><li onclick="handleItemClick()">sea salt and paprika to taste </li><li onclick="handleItemClick()">2 cups mozzarella (or several types of) cheese, grated <i class="conditional-space-after">or</i>1½ cups Nutritional yeast flakes </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Entrees_BellPepperCasserole', '<p><ol><li onclick="handleItemClick()">Preheat oven to 350 degrees. </li><li onclick="handleItemClick()">In a large saucepan, warm up half the amount of olive oil and sautee the onions for 1-2 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1446\')" class="alarm-clock-icon"><div id="circular-timer-1446" class="circular-timer-container"><script>new CircularTimer("circular-timer-1446", 60)</script><script>new CircularTimer("circular-timer-1446", 120)</script></li><li onclick="handleItemClick()">Add the carrots and the peppers. </li><li onclick="handleItemClick()">Cook on high flame for about 2-3 minutes, stirring constantly. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1447\')" class="alarm-clock-icon"><div id="circular-timer-1447" class="circular-timer-container"><script>new CircularTimer("circular-timer-1447", 120)</script><script>new CircularTimer("circular-timer-1447", 180)</script></li><li onclick="handleItemClick()">Add the remaining oil and the tomatoes. </li><li onclick="handleItemClick()">Lower the flame, cover and simmer for about 4 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1448\')" class="alarm-clock-icon"><div id="circular-timer-1448" class="circular-timer-container"><script>new CircularTimer("circular-timer-1448", 240)</script></li><li onclick="handleItemClick()">Add sea salt, paprika and garlic; mix well. </li><li onclick="handleItemClick()">Lightly oil a casserole dish. </li><li onclick="handleItemClick()">Put half of the vegetable mix on the bottom of it. </li><li onclick="handleItemClick()">Put brown rice on top of the vegetable mix. </li><li onclick="handleItemClick()">Layer black beans over the rice. </li><li onclick="handleItemClick()">Pour the rest of the vegetable mixture on top. </li><li onclick="handleItemClick()">Sprinkle with cheese or nutritional yeast flakes. (If using flakes, lightly mix them with the vegetable layer.) </li><li onclick="handleItemClick()">Top with cracker bits. </li><li onclick="handleItemClick()">Bake for 35 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1449\')" class="alarm-clock-icon"><div id="circular-timer-1449" class="circular-timer-container"><script>new CircularTimer("circular-timer-1449", 2100)</script></li><li onclick="handleItemClick()">Serve with raw avocado sour cream. </li></ol>' ] );
