ingredients_array.push( [ 'CostanzoRecipes_Entrees_ArrabbiataSauce', '<h3 onclick="toggleSection(\'1174\')">Single Recipe<span id="heading-ellipses-1174"></h3><div id=\'heading-content-1174\'><p><ul><li onclick="handleItemClick()">½ tablespoon plus ½ teaspoon olive oil </li><li onclick="handleItemClick()">2 cups chopped onion </li><li onclick="handleItemClick()">8 cloves garlic, minced </li><li onclick="handleItemClick()">¾ cup red wine </li><li onclick="handleItemClick()">⅛ cup white sugar </li><li onclick="handleItemClick()">⅛ cup chopped fresh basil </li><li onclick="handleItemClick()">½ tablespoon plus ½ teaspoon crushed red pepper flakes </li><li onclick="handleItemClick()">¼ cup tomato paste </li><li onclick="handleItemClick()">⅛ cup lemon juice </li><li onclick="handleItemClick()">1 teaspoon Italian seasoning </li><li onclick="handleItemClick()">½ teaspoon ground black pepper </li><li onclick="handleItemClick()">4 (14.5 ounce) cans peeled and diced tomatoes </li><li onclick="handleItemClick()">¼ cup chopped fresh parsley </li></ul></div>  <!-- <h3> --><h3 onclick="toggleSection(\'1175\')">Double Recipe<span id="heading-ellipses-1175"></h3><div id=\'heading-content-1175\'><p><ul><li onclick="handleItemClick()">1 tablespoon plus 1 teaspoon olive oil </li><li onclick="handleItemClick()">4 cups chopped onion </li><li onclick="handleItemClick()">16 cloves garlic, minced </li><li onclick="handleItemClick()">1½  cups red wine </li><li onclick="handleItemClick()">¼ cup white sugar </li><li onclick="handleItemClick()">¼ cup chopped fresh basil </li><li onclick="handleItemClick()">1 tablespoon plus 1 teaspoon crushed red pepper flakes </li><li onclick="handleItemClick()">½ cup tomato paste </li><li onclick="handleItemClick()">¼ cup lemon juice </li><li onclick="handleItemClick()">2 teaspoons Italian seasoning </li><li onclick="handleItemClick()">1 teaspoon ground black pepper </li><li onclick="handleItemClick()">8 (14.5 ounce) cans peeled and diced tomatoes </li><li onclick="handleItemClick()">½ cup chopped fresh parsley </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Entrees_ArrabbiataSauce', '<p><ol><li onclick="handleItemClick()">Heat oil in a large skillet or saucepan over medium heat. </li><li onclick="handleItemClick()">Sauté onion and garlic in oil for 5 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1433\')" class="alarm-clock-icon"><div id="circular-timer-1433" class="circular-timer-container"><script>new CircularTimer("circular-timer-1433", 300)</script></li><li onclick="handleItemClick()">Stir in wine, sugar, basil, red pepper, tomato paste, lemon juice, Italian seasoning, black pepper and tomatoes. </li><li onclick="handleItemClick()">Bring to a boil. </li><li onclick="handleItemClick()">Reduce heat to medium and simmer uncovered for about 15 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1434\')" class="alarm-clock-icon"><div id="circular-timer-1434" class="circular-timer-container"><script>new CircularTimer("circular-timer-1434", 900)</script></li><li onclick="handleItemClick()">Stir in parsley. </li><li onclick="handleItemClick()">Ladle over hot cooked pasta of your choice. </li></ol>' ] );
