ingredients_array.push( [ 'CostanzoRecipes_Desserts_PumpkinCookies', '<p><ul><li onclick="handleItemClick()">½ cup (113g) butter </li><li onclick="handleItemClick()">1½ (300g) cups sugar </li><li onclick="handleItemClick()">1 egg </li><li onclick="handleItemClick()">1 cup (225g) cooked or canned pumpkin (unspiced) </li><li onclick="handleItemClick()">1 teaspoon vanilla </li><li onclick="handleItemClick()">2½ cups (300g) flour </li><li onclick="handleItemClick()">1 teaspoon baking powder </li><li onclick="handleItemClick()">1 teaspoon baking soda </li><li onclick="handleItemClick()">½ teaspoon salt </li><li onclick="handleItemClick()">1 teaspoon nutmeg </li><li onclick="handleItemClick()">1 teaspoon cinnamon </li><li onclick="handleItemClick()">½ cup (140g) diced or slivered roasted almonds </li><li onclick="handleItemClick()">1 cup (175g) chocolate pieces or chips </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Desserts_PumpkinCookies', '<p><ol><li onclick="handleItemClick()">Grease cookie sheets or use silpats. </li><li onclick="handleItemClick()">Preheat oven to 350 degrees. </li><li onclick="handleItemClick()">Cream butter and sugar together until light and fluffy. </li><li onclick="handleItemClick()">Beat in egg, pumpkin and vanilla. </li><li onclick="handleItemClick()">Sift flour, baking powder, baking soda, salt, nutmeg and cinnamon. Add to creamed mixture and mix well. </li><li onclick="handleItemClick()">Add almonds and chocolate and mix thoroughly. </li><li onclick="handleItemClick()">Drop by teaspoonful onto cookie sheets. </li><li onclick="handleItemClick()">Bake until lightly browned, about 15 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1409\')" class="alarm-clock-icon"><div id="circular-timer-1409" class="circular-timer-container"><script>new CircularTimer("circular-timer-1409", 900)</script></li><li onclick="handleItemClick()">Remove from cookie sheets while still warm and cool on racks. </li></ol>' ] );
