ingredients_array.push( [ 'CostanzoRecipes_Desserts_PerfectChocolateCake', '<h3 onclick="toggleSection(\'1156\')">Cake<span id="heading-ellipses-1156"></h3><div id=\'heading-content-1156\'><p><ul><li onclick="handleItemClick()">1 cup cocoa (unsweetened) </li><li onclick="handleItemClick()">2 cup boiling water </li><li onclick="handleItemClick()">2¾ cup all purpose flour </li><li onclick="handleItemClick()">2 t baking soda </li><li onclick="handleItemClick()">½ t salt </li><li onclick="handleItemClick()">½ t baking powder </li><li onclick="handleItemClick()">1 cup butter </li><li onclick="handleItemClick()">2½ cup sugar </li><li onclick="handleItemClick()">4 eggs </li><li onclick="handleItemClick()">1½ t pure vanilla extract </li></ul></div>  <!-- <h3> --><h3 onclick="toggleSection(\'1157\')">Buttercream Frosting<span id="heading-ellipses-1157"></h3><div id=\'heading-content-1157\'><p><ul><li onclick="handleItemClick()">6 ounces unsweetened baking chocolate </li><li onclick="handleItemClick()">1 cup butter </li><li onclick="handleItemClick()">½ cup heavy cream </li><li onclick="handleItemClick()">2½ cups powered sugar, sifted </li></ul></div>  <!-- <h3> --><h3 onclick="toggleSection(\'1158\')">Filling<span id="heading-ellipses-1158"></h3><div id=\'heading-content-1158\'><p><ul><li onclick="handleItemClick()">1 cup heavy cream </li><li onclick="handleItemClick()">¼ cup powered sugar </li><li onclick="handleItemClick()">1 tablespoon pure vanilla extract </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Desserts_PerfectChocolateCake', '<p><ul><li onclick="handleItemClick()">Preheat the oven to 350 degrees. </li><li onclick="handleItemClick()">Butter and flour three 9" round cake pans (line bottoms with parchment or waxed paper). </li><li onclick="handleItemClick()">Combine cocoa with 2 cups boiling water, stirring until smooth. </li><li onclick="handleItemClick()">Cool completely. </li><li onclick="handleItemClick()">Sift together dry ingredients. </li><li onclick="handleItemClick()">In a large bowl beat together butter, sugar, eggs and vanilla. </li><li onclick="handleItemClick()">Alternate adding the dry ingredients and the cocoa mixture to the creamed mixture. Do not over mix. Blend just enough to moisten the dry ingredients. </li><li onclick="handleItemClick()">Divide the batter evenly into cake pans. </li><li onclick="handleItemClick()">Bake 25-30 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1407\')" class="alarm-clock-icon"><div id="circular-timer-1407" class="circular-timer-container"><script>new CircularTimer("circular-timer-1407", 1500)</script><script>new CircularTimer("circular-timer-1407", 1800)</script></li><li onclick="handleItemClick()">Cool in the pans for 10 minutes then remove the cakes from the pans to cool completely before frosting. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1408\')" class="alarm-clock-icon"><div id="circular-timer-1408" class="circular-timer-container"><script>new CircularTimer("circular-timer-1408", 600)</script></li><li onclick="handleItemClick()">While the cake is cooling, prepare the frosting. </li><li onclick="handleItemClick()">In a medium saucepan, melt the chocolate and butter. </li><li onclick="handleItemClick()">Stir in the cream until smooth. </li><li onclick="handleItemClick()">Remove the pan from the stove and place it in a large bowl filled with ice. </li><li onclick="handleItemClick()">Using a whisk or electric mixer, beat in the powdered sugar until the frosting holds a stiff shape. Chill. </li><li onclick="handleItemClick()">Whip all the filling ingredients together until stiff. Chill. </li><li onclick="handleItemClick()">Construct the cake when all parts are cool. </li><li onclick="handleItemClick()">Spread the filling between the layers and the frosting on the top and sides. </li><li onclick="handleItemClick()">Plan to serve the cake soon after completion or provide a large space in the refrigerator to keep it cool until it is served. </li></ul><p><div class="note">Note Note Note Note </div><p><div class="warning">Warning: moo Warning: moo Warning: moo Warning: moo </div><p><div class="tip">Tip Tip Tip Tip </div><p><div class="info">Some info Some info Some info Some info </div>' ] );
