ingredients_array.push( [ 'CostanzoRecipes_Desserts_PeachMelbaCreamCake', '<h3 onclick="toggleSection(\'1152\')">Ingredients:<span id="heading-ellipses-1152"></h3><div id=\'heading-content-1152\'><p><ul><li onclick="handleItemClick()">¾ cup softened, unsalted butter plus more for greasing the pans </li><li onclick="handleItemClick()">1 cup sugar </li><li onclick="handleItemClick()">½ cup brown sugar </li><li onclick="handleItemClick()">3 eggs </li><li onclick="handleItemClick()">1½ teaspoons pure vanilla extract </li><li onclick="handleItemClick()">finely grated zest of 1 lemon </li><li onclick="handleItemClick()">¼ cup fresh lemon juice </li><li onclick="handleItemClick()">2½ cups all purpose flour </li><li onclick="handleItemClick()">1½ teaspoons baking soda </li><li onclick="handleItemClick()">½ teaspoon salt </li><li onclick="handleItemClick()">1½ cups buttermilk </li><li onclick="handleItemClick()">4 pounds ripe peaches </li><li onclick="handleItemClick()">2 tablespoons fresh lemon juice </li><li onclick="handleItemClick()">2½ tablespoons sugar </li><li onclick="handleItemClick()">2 cups heavy cream </li><li onclick="handleItemClick()">1½ teaspoons pure vanilla extract </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Desserts_PeachMelbaCreamCake', '</div>  <!-- <h3> --><h3 onclick="toggleSection(\'1153\')">Method:<span id="heading-ellipses-1153"></h3><div id=\'heading-content-1153\'><p><ol><li onclick="handleItemClick()">Raspberry Melba Sauce (see recipe) </li><li onclick="handleItemClick()">Preheat the oven to 350 degrees F. Butter and flour two 9-inch cake pans (line with buttered and floured wax paper for easier removal, optional). </li><li onclick="handleItemClick()">Cream the ¾ cup butter and sugars in a mixing bowl. Addd the eggs, one at a time, beating well after each adition. Beat in the vanilla, lemon zest and juice. </li><li onclick="handleItemClick()">Sift together the flour, baking soda and salt; add to mixture in the bowl, alternating with buttermilk, to make batter. </li><li onclick="handleItemClick()">Scrape batter into the pans. Bake until a toothpick inserted in the center comes out clean, about 35 to 40 minutes. Cool the cakes in their pans on a rack for 15 minutes, then turn them out onto the rack, peel off wax paper and cool completely. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1405\')" class="alarm-clock-icon"><div id="circular-timer-1405" class="circular-timer-container"><script>new CircularTimer("circular-timer-1405", 2100)</script><script>new CircularTimer("circular-timer-1405", 2400)</script><script>new CircularTimer("circular-timer-1405", 900)</script></li><li onclick="handleItemClick()">Shortly before serving, peel, pit and slice the peaches. Toss with lemon juice and 1 tablespoon sugar; set aside for 15 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1406\')" class="alarm-clock-icon"><div id="circular-timer-1406" class="circular-timer-container"><script>new CircularTimer("circular-timer-1406", 900)</script></li><li onclick="handleItemClick()">Whip the cream with the vanilla and the remaining 1½ tablespoons of sugar until it holds soft peaks. </li><li onclick="handleItemClick()">Place on cake on a serving platter. Spread with half the cream, then half the peaches. Cover with second cake and top with remaining peaches. Top with a dollop of cream and, if desired, a mint sprig. Serve immediately, with the Raspberry Melba Sauce on the side or drizzled over the cake. </li><li onclick="handleItemClick()">Serves 10.  </li><li onclick="handleItemClick()">Per serving: 651 calories, 82g carbs, 9g protein, 34 g fat, 167 mg cholesterol. </li></ol>' ] );
ingredients_array.push( [ 'CostanzoRecipes_Desserts_PeachMelbaCreamCake', '</div>  <!-- <h3> --><h3 onclick="toggleSection(\'1154\')">Ingredients:<span id="heading-ellipses-1154"></h3><div id=\'heading-content-1154\'><p><ul><li onclick="handleItemClick()">2 cups raspberries </li><li onclick="handleItemClick()">½ cup confectioners\' sugar </li><li onclick="handleItemClick()">1 tablespoon fresh lemon juice </li><li onclick="handleItemClick()">¼ cup apple juice </li></ul>' ] );
