ingredients_array.push( [ 'CostanzoRecipes_Desserts_PeachCobblerCoffeeCake', '<h3 onclick="toggleSection(\'1149\')">Coffee Cake<span id="heading-ellipses-1149"></h3><div id=\'heading-content-1149\'><p><ul><li onclick="handleItemClick()">2 c. all purpose flour </li><li onclick="handleItemClick()">2½ tsp. baking powder </li><li onclick="handleItemClick()">1 tsp. cinnamon </li><li onclick="handleItemClick()">¼ c. milk </li><li onclick="handleItemClick()">¾ c. brown sugar </li><li onclick="handleItemClick()">½ c. sour cream </li><li onclick="handleItemClick()">¼ c. butter softened </li><li onclick="handleItemClick()">1 egg </li><li onclick="handleItemClick()">1 tsp. vanilla </li><li onclick="handleItemClick()">2 c. sliced peaches </li></ul></div>  <!-- <h3> --><h3 onclick="toggleSection(\'1150\')">Crumb Topping<span id="heading-ellipses-1150"></h3><div id=\'heading-content-1150\'><p><ul><li onclick="handleItemClick()">1 c. brown sugar </li><li onclick="handleItemClick()">⅔ c. flour </li><li onclick="handleItemClick()">6 Tbsp. softened butter </li></ul></div>  <!-- <h3> --><h3 onclick="toggleSection(\'1151\')">Glaze<span id="heading-ellipses-1151"></h3><div id=\'heading-content-1151\'><p><ul><li onclick="handleItemClick()">½ c. powdered sugar </li><li onclick="handleItemClick()">2½ tsp. milk </li><li onclick="handleItemClick()">⅛ tsp. cinnamon </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Desserts_PeachCobblerCoffeeCake', '<p><ol><li onclick="handleItemClick()">In a medium bowl, combine 2 c. flour, baking powder and cinnamon. Set aside. </li><li onclick="handleItemClick()">Beat ¼ c. milk, ¾ c. brown sugar, ½ c. sour cream, ¼ c. butter, egg, and vanilla until creamy. Add dry ingredients and mix just until blended. </li><li onclick="handleItemClick()">Fold in peaches. </li><li onclick="handleItemClick()">Make Crumble Topping: Combine 1 c. brown sugar, ⅔ c. flour in a bowl. Cut in 6 Tbsp. softened butter until mixture is crumbly. </li><li onclick="handleItemClick()">Spoon batter into a 10 inch springform pan and sprinkle with crumble topping. </li><li onclick="handleItemClick()">Bake at 350 degrees for 50-55 minutes or until center is set and top is golden brown. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1404\')" class="alarm-clock-icon"><div id="circular-timer-1404" class="circular-timer-container"><script>new CircularTimer("circular-timer-1404", 3000)</script><script>new CircularTimer("circular-timer-1404", 3300)</script></li><li onclick="handleItemClick()">Combine ingredients for glaze and drizzle over baked coffee cake. </li></ol>' ] );
credits_array.push( [ 'CostanzoRecipes_Desserts_PeachCobblerCoffeeCake', '<p><ul><li onclick="handleItemClick()">From <a href="https://www.lemontreedwelling.com/peach-cobbler-coffee-cake/" target="_blank">https://www.lemontreedwelling.com/peach-cobbler-coffee-cake/</a> </li><li onclick="handleItemClick()">date created: [2020-06-20 Sat]  </li><li onclick="handleItemClick()">[2020-06-20 Sat] Made for Penny and Gary. Moist and tasty. I might want to hold back a bit on the sugar. </li></ul>' ] );
ingredients_array.push( [ 'CostanzoRecipes_Desserts_OatmealChocolateMonsters', '<p><ul><li onclick="handleItemClick()">1 cup butter </li><li onclick="handleItemClick()">1 cup sugar </li><li onclick="handleItemClick()">1 cup brown sugar </li><li onclick="handleItemClick()">2 eggs </li><li onclick="handleItemClick()">1 teaspoon vanilla </li><li onclick="handleItemClick()">2 cups flour </li><li onclick="handleItemClick()">2 ½ cups rolled oats </li><li onclick="handleItemClick()">1 teaspoon baking powder </li><li onclick="handleItemClick()">1 teaspoon baking soda </li><li onclick="handleItemClick()">12 ounces chocolate chips </li><li onclick="handleItemClick()">1 ½ cups chopped nuts (<i class="conditional-space-after">optional but highly recommended</i>) </li><li onclick="handleItemClick()">4 ounces grated chocolate (<i class="conditional-space-after">optional</i>) </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Desserts_OatmealChocolateMonsters', '<p><ol><li onclick="handleItemClick()">Preheat oven to 375°F. </li><li onclick="handleItemClick()">In a large bowl, cream together the butter an sugars. </li><li onclick="handleItemClick()">Add eggs and vanilla and mix well. </li><li onclick="handleItemClick()">In a medium bowl, mix together the dry ingredients; fold into mixture in large bowl. </li><li onclick="handleItemClick()">Add chocolate chips, nuts and grated chocolate (if using) and stir to combine. </li><li onclick="handleItemClick()">Drop large spoonfuls onto an ungreased baking sheet. </li><li onclick="handleItemClick()">Bake until brown, about 12 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1403\')" class="alarm-clock-icon"><div id="circular-timer-1403" class="circular-timer-container"><script>new CircularTimer("circular-timer-1403", 720)</script></li></ol>' ] );
