ingredients_array.push( [ 'CostanzoRecipes_Desserts_LemonCornmealCakewithLemonGlazeandCrushedBlueberrySauce', '<p><ul><li onclick="handleItemClick()">1½ cups (packed) powdered sugar, sifted </li><li onclick="handleItemClick()">2 tablespoons (or more) fresh lemon juice </li><li onclick="handleItemClick()">1½ cups all purpose flour </li><li onclick="handleItemClick()">⅓ cup yellow cornmeal </li><li onclick="handleItemClick()">¾ cup sugar </li><li onclick="handleItemClick()">3½ teaspoons baking powder </li><li onclick="handleItemClick()">½ teaspoon salt </li><li onclick="handleItemClick()">1 cup buttermilk </li><li onclick="handleItemClick()">2 large eggs </li><li onclick="handleItemClick()">1 tablespoon finely grated lemon peel </li><li onclick="handleItemClick()">¾ teaspoon vanilla extract </li><li onclick="handleItemClick()">½ cup (1 stick) unsalted butter, melted, cooled </li><li onclick="handleItemClick()">Crushed-Blueberry Sauce </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Desserts_LemonCornmealCakewithLemonGlazeandCrushedBlueberrySauce', '<p><ol><li onclick="handleItemClick()">For glaze: Combine powdered sugar and 2 tablespoons lemon juice in small bowl. Stir with spoon until smooth and paste-like, adding more lemon juice by ½ teaspoonfuls if glaze is too thick to spread. Set aside. </li><li onclick="handleItemClick()">For cake: Position rack in center of oven and preheat to 350 degrees F. Butter 9-inch-diameter cake pan with 2-inch-high sides; line bottom with parchment. Combine flour, cornmeal, sugar, baking powder, and salt in large bowl; whisk to blend. Whisk buttermilk, eggs, lemon peel, and vanilla in small bowl. Pour buttermilk mixture and melted butter into flour mixture. Using rubber spatula, gently fold liquids into flour mixture until just blended (do not stir). Scrape batter into pan; spread evenly. </li><li onclick="handleItemClick()">Bake cake until tester inserted into center comes out clean and cake pulls away from sides of pan, about 30 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1396\')" class="alarm-clock-icon"><div id="circular-timer-1396" class="circular-timer-container"><script>new CircularTimer("circular-timer-1396", 1800)</script></li><li onclick="handleItemClick()">Immediately run knife around sides of cake. Place rack atop cake in pan. Using oven mitts, hold pan and rack firmly together and invert cake onto rack. Remove pan from cake. Place another rack on bottom of cake; invert 1 more time so that cake is top side up. Stir glaze until blended. While cake is still very hot, drop glaze by tablespoonfuls onto cake; spread to within ½ inch of edge (some glaze may drip down sides of cake). Cool completely. Serve with Crushed-Blueberry Sauce. </li></ol>' ] );
credits_array.push( [ 'CostanzoRecipes_Desserts_LemonCornmealCakewithLemonGlazeandCrushedBlueberrySauce', '<p><ul><li onclick="handleItemClick()">Bon Appetit </li><li onclick="handleItemClick()">April 2009 </li><li onclick="handleItemClick()">by Abigail Johnson Dodge </li><li onclick="handleItemClick()">[TC made on 29-June-12. Yum! (Don\'t forget the baking powder!)] </li><li onclick="handleItemClick()">You can view the complete recipe online at: <a href="http://www.epicurious.com/recipes/food/views/Lemon-Cornmeal-Cake-with-Lemon-Glaze-and-Crushed-Blueberry-Sauce-352048?mbid=ipapp" target="_blank">http://www.epicurious.com/recipes/food/views/Lemon-Cornmeal-Cake-with-Lemon-Glaze-and-Crushed-Blueberry-Sauce-352048?mbid=ipapp</a> </li><li onclick="handleItemClick()">date created: [2012-07-02 Mon]  </li></ul>' ] );
ingredients_array.push( [ 'CostanzoRecipes_Desserts_GingerSnaps', '<p><ul><li onclick="handleItemClick()">4-July-06: </li><li onclick="handleItemClick()">Needs much more ginger! used 1 tsp + 2 tsp fresh... not enough! </li><li onclick="handleItemClick()">A bit too sweet. </li></ul><p><ul><li onclick="handleItemClick()">2 cups all-purpose flour </li><li onclick="handleItemClick()">1 teaspoon ground ginger </li><li onclick="handleItemClick()">1 teaspoon ground cinnamon </li><li onclick="handleItemClick()">2 teaspoons baking soda </li><li onclick="handleItemClick()">½ teaspoon salt </li><li onclick="handleItemClick()">¾ cup vegetable shortening </li><li onclick="handleItemClick()">1 cup granulated sugar </li><li onclick="handleItemClick()">¼ cup light molasses </li><li onclick="handleItemClick()">1 large egg </li><li onclick="handleItemClick()">Sugar for rolling </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Desserts_GingerSnaps', '<p><ol><li onclick="handleItemClick()">Preheat oven to 350*F (175*C). </li><li onclick="handleItemClick()">In medium bowl combine flour, ginger, cinnamon, baking soda and salt; set aside. </li><li onclick="handleItemClick()">Cream together shortening and sugar, add egg and beat until fluffy. Stir in molasses and mix well. Stir in flour mixture until just mixed </li><li onclick="handleItemClick()">Form tablespoonfuls of dough into round balls. Roll tops of balls in sugar. Place on a cookie sheet about 2-inches apart. (These cookies flatten as they bake.) Bake for exactly 9 minutes. Transfer cookies to wire racks to cool completely.  <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1395\')" class="alarm-clock-icon"><div id="circular-timer-1395" class="circular-timer-container"><script>new CircularTimer("circular-timer-1395", 540)</script></li></ol>' ] );
