credits_array.push( [ 'CostanzoRecipes_Desserts_DoubleLemonPoppySeedBundtCake', '<p><ul><li onclick="handleItemClick()">Per Serving <ul><li onclick="handleItemClick()">Calories: 201 kcal </li><li onclick="handleItemClick()">Carbohydrates: 35 g </li><li onclick="handleItemClick()">Dietary Fiber: 1 g </li><li onclick="handleItemClick()">Fat: 5 g </li><li onclick="handleItemClick()">Protein: 4 g </li><li onclick="handleItemClick()">Sugars: 22 g </li></ul></li><li onclick="handleItemClick()"><a href="http://food.yahoo.com/recipes/eatingwell/285/double-lemon-poppy-seed-bundt-cake/print" target="_blank">http://food.yahoo.com/recipes/eatingwell/285/double-lemon-poppy-seed-bundt-cake/print</a> </li><li onclick="handleItemClick()">23 May 2009 </li><li onclick="handleItemClick()">date created: [2009-05-26 Tue]  </li></ul>' ] );
ingredients_array.push( [ 'CostanzoRecipes_Desserts_DoubleChocolateCupcakes', '<h3 onclick="toggleSection(\'1143\')">For the cupcakes<span id="heading-ellipses-1143"></h3><div id=\'heading-content-1143\'><p><ul><li onclick="handleItemClick()">1 cup all-purpose flour </li><li onclick="handleItemClick()">1 cup granulated sugar </li><li onclick="handleItemClick()">½ teaspoon baking powder </li><li onclick="handleItemClick()">½ teaspoon baking soda </li><li onclick="handleItemClick()">½ teaspoon salt </li><li onclick="handleItemClick()">½ cup unsweetened cocoa powder </li><li onclick="handleItemClick()">½ cup vegetable oil </li><li onclick="handleItemClick()">1 cup hot water </li><li onclick="handleItemClick()">1 large egg </li></ul></div>  <!-- <h3> --><h3 onclick="toggleSection(\'1144\')">For the chocolate buttercream<span id="heading-ellipses-1144"></h3><div id=\'heading-content-1144\'><p><ul><li onclick="handleItemClick()">1 cup (2 sticks) unsalted butter </li><li onclick="handleItemClick()">4 ounces unsweetened chocolate, melted and cooled for 5 minutes <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1376\')" class="alarm-clock-icon"><div id="circular-timer-1376" class="circular-timer-container"><script>new CircularTimer("circular-timer-1376", 300)</script></li><li onclick="handleItemClick()">1 pound powdered sugar </li><li onclick="handleItemClick()">¼ cup unsweetened cocoa powder </li><li onclick="handleItemClick()">2 teaspoons pure vanilla extract </li><li onclick="handleItemClick()">1-3 tablespoons milk </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Desserts_DoubleChocolateCupcakes', '</div>  <!-- <h3> --><h3 onclick="toggleSection(\'1145\')">For the cupcakes<span id="heading-ellipses-1145"></h3><div id=\'heading-content-1145\'><p><ol><li onclick="handleItemClick()">Preheat oven to 350°F. </li><li onclick="handleItemClick()">Line a 12-cup standard muffin tin with paper liners. </li><li onclick="handleItemClick()">In a large bowl, whisk together the flour, sugar, baking powder, baking soda, salt and cocoa powder. </li><li onclick="handleItemClick()">Add in vegetable oil, hot water, egg and vanilla and whisk until well-combined and smooth. (Batter will be thin.) </li><li onclick="handleItemClick()">Use a large scoop (about 3 tablespoons) to divide the batter between lined muffin cups. </li><li onclick="handleItemClick()">Bake in preheated oven for 17-20 minutes, until the tops spring back when touched lightly. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1377\')" class="alarm-clock-icon"><div id="circular-timer-1377" class="circular-timer-container"><script>new CircularTimer("circular-timer-1377", 1020)</script><script>new CircularTimer("circular-timer-1377", 1200)</script></li><li onclick="handleItemClick()">Place on a cooling rack until cupcakes are cool to the touch. </li></ol></div>  <!-- <h3> --><h3 onclick="toggleSection(\'1146\')">For the chocolate buttercream<span id="heading-ellipses-1146"></h3><div id=\'heading-content-1146\'><p><ol><li onclick="handleItemClick()">In the bowl of a stand mixer fitted with the paddle attachment, beat butter on medium-high speed for about 6 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1378\')" class="alarm-clock-icon"><div id="circular-timer-1378" class="circular-timer-container"><script>new CircularTimer("circular-timer-1378", 360)</script><div class="note">   This process will lighten the color of the butter and ensure your buttercream is extra fluffy. </div></li><li onclick="handleItemClick()">Add cooled, melted chocolate to the butter and mix until combined, scraping down the sides of the bowl as needed. </li><li onclick="handleItemClick()">Sift together the powdered sugar and cocoa powder. </li><li onclick="handleItemClick()">With the mixer on low speed, gradually add in the powdered sugar mixture and continue mixing on low speed until the powdered sugar is completely incorporated. </li><li onclick="handleItemClick()">Add in 1-2 tablespoons milk and vanilla extract. mixing to combine. </li><li onclick="handleItemClick()">Turn mixer back up to medium-high speed and beat the mixture for an additional 5 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1379\')" class="alarm-clock-icon"><div id="circular-timer-1379" class="circular-timer-container"><script>new CircularTimer("circular-timer-1379", 300)</script></li><li onclick="handleItemClick()">If needed, add a little more milk, 1 teaspoon at a time until you reach the consistency you desire. </li><li onclick="handleItemClick()">Pipe buttercream onto cooled cupcakes. <div class="tip">   If desired, top with chocolate sprinkles or mini chocolate chips. </div></li></ol>' ] );
credits_array.push( [ 'CostanzoRecipes_Desserts_DoubleChocolateCupcakes', '<p><ul><li onclick="handleItemClick()">From <a href="http://chocolatechocolateandmore.com/double-chocolate-cupcakes" target="_blank">http://chocolatechocolateandmore.com/double-chocolate-cupcakes</a>via Louise </li><li onclick="handleItemClick()">date created: [2020-02-21 Fri]  </li></ul>' ] );
ingredients_array.push( [ 'CostanzoRecipes_Desserts_CongoBars', '<p><ul><li onclick="handleItemClick()">1½ cups unsweetened shredded coconut </li><li onclick="handleItemClick()">1½ cups (7½ ounces) all-purpose flour </li><li onclick="handleItemClick()">1 teaspoon baking powder </li><li onclick="handleItemClick()">½ teaspoon salt </li><li onclick="handleItemClick()">1½ cups packed (10½ ounces) light brown sugar </li><li onclick="handleItemClick()">12 tablespoons unsalted butter, melted and cooled </li><li onclick="handleItemClick()">2 large eggs </li><li onclick="handleItemClick()">1½ teaspoons vanilla extract </li><li onclick="handleItemClick()">1 cup pecans, toasted and coarsely chopped </li><li onclick="handleItemClick()">1 cup (6 ounces) semisweet chocolate chips </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Desserts_CongoBars', '<p><ol><li onclick="handleItemClick()">Adjust an oven rack to the middle position and heat oven to 350° </li><li onclick="handleItemClick()">Make a foil sling for a 13 by 9-inch baking pan by folding 2 long sheets of aluminum foil; the first sheet should be 13 inches wide and the second sheet should be 9 inches wide. Lay sheets of foil in pan perpendicular to one another, with extra foil hanging over the edges of the pan. Push foil into corners and up the sides of the pan, smoothing foil flush to the pan. Grease foil and set pan aside. </li><li onclick="handleItemClick()">Toast coconut on rimmed baking sheet, stirring two or three times, until lightly golden, 4 to 5 minutes. Let cool. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1374\')" class="alarm-clock-icon"><div id="circular-timer-1374" class="circular-timer-container"><script>new CircularTimer("circular-timer-1374", 240)</script><script>new CircularTimer("circular-timer-1374", 300)</script></li><li onclick="handleItemClick()">Whisk flour, baking powder and salt together in  medium bowl; set aside. </li><li onclick="handleItemClick()">Whisk brown sugar and melted butter together in a medium bowl until combined. </li><li onclick="handleItemClick()">Add eggs and vanilla and mix well. </li><li onclick="handleItemClick()">Using rubber spatula, fold dry ingredients into egg mixture until just combined. Do not overmix. </li><li onclick="handleItemClick()">Fold in nuts, chocolate chips and coconut. </li><li onclick="handleItemClick()">Transfer batter to prepared pan, smoothing top with rubber spatula. </li><li onclick="handleItemClick()">Bake until top is shiny and cracked and feels firm to the touch, 22 to 25 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1375\')" class="alarm-clock-icon"><div id="circular-timer-1375" class="circular-timer-container"><script>new CircularTimer("circular-timer-1375", 1320)</script><script>new CircularTimer("circular-timer-1375", 1500)</script></li><li onclick="handleItemClick()">Transfer pan to wire rack and let cool completely. </li><li onclick="handleItemClick()">Remove from pan, using foil. </li><li onclick="handleItemClick()">Cut into 2 by 1½ inch bars and serve. </li></ol>' ] );
credits_array.push( [ 'CostanzoRecipes_Desserts_CongoBars', '<p><ul><li onclick="handleItemClick()">From Cook’s Illustrated Baking Book, page 240 </li><li onclick="handleItemClick()">date created: [2021-05-27 Thu]  </li><li onclick="handleItemClick()">[2021-05-25 Tue] Made for T’s 60<sup>th</sup>birthday and they were well received! </li></ul>' ] );
ingredients_array.push( [ 'CostanzoRecipes_Desserts_CoconutLayerCake', '<h3 onclick="toggleSection(\'1138\')">Ingredients<span id="heading-ellipses-1138"></h3><div id=\'heading-content-1138\'><p><ul><li onclick="handleItemClick()">1 cup (2 sticks) unsalted butter, softened, plus more for coating pans  </li><li onclick="handleItemClick()">3 cups cake flour, plus more for coating pans  </li><li onclick="handleItemClick()">1 tsp. baking powder  </li><li onclick="handleItemClick()">½ tsp. baking soda  </li><li onclick="handleItemClick()">½ tsp. salt  </li><li onclick="handleItemClick()">1¾ cups sugar  </li><li onclick="handleItemClick()">1 cup Coco GOYA® Cream of Coconut  </li><li onclick="handleItemClick()">4 large eggs, separated  </li><li onclick="handleItemClick()">1 tsp. GOYA® Vanilla Extract  </li><li onclick="handleItemClick()">1 cup GOYA® Coconut Milk  </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Desserts_CoconutLayerCake', '</div>  <!-- <h3> --><h3 onclick="toggleSection(\'1139\')">Method:<span id="heading-ellipses-1139"></h3><div id=\'heading-content-1139\'><p><ol><li onclick="handleItemClick()">Adjust oven racks to upper and lower thirds; heat oven to 350 degreesF. Butter three 9-inch-diameter cake pans. Line bottom of pans with parchment paper rounds; butter parchment paper. Dust bottom and sides of pans with flour, knocking out excess. Set pans in refrigerator. </li><li onclick="handleItemClick()">In medium bowl, using whisk, whisk together flour, baking powder, baking soda and salt; set aside. </li><li onclick="handleItemClick()">In large mixing bowl, using electric mixer, beat together sugar, butter and cream of coconut on medium-high speed until fluffy, about 2 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1369\')" class="alarm-clock-icon"><div id="circular-timer-1369" class="circular-timer-container"><script>new CircularTimer("circular-timer-1369", 120)</script></li><li onclick="handleItemClick()">Beat in egg yolks, one at a time, and vanilla extract until well combined. </li><li onclick="handleItemClick()">On low speed, beat in reserved flour mixture, scraping sides of bowl with spatula, until incorporated. </li><li onclick="handleItemClick()">Beat in 1 cup coconut milk until just blended; set aside. </li><li onclick="handleItemClick()">In separate medium mixing bowl, using clean dry beaters, beat egg whites until stiff but not dry. Using spatula, stir ⅓ beaten egg whites into cake batter until well blended. Gently fold in remaining beaten egg whites until well combined. </li><li onclick="handleItemClick()">Divide cake batter evenly among prepared pans. Bake cakes, rotating after about 15 minutes, until tops are golden brown and cake tester inserted into center comes out clean, 30-32 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1370\')" class="alarm-clock-icon"><div id="circular-timer-1370" class="circular-timer-container"><script>new CircularTimer("circular-timer-1370", 900)</script><script>new CircularTimer("circular-timer-1370", 1800)</script><script>new CircularTimer("circular-timer-1370", 1920)</script></li><li onclick="handleItemClick()">Cool cakes in pans on cooling racks about 15 minutes. Loosen cakes from sides of pans with thin knife, invert cakes onto racks. Cool cakes to room temperature; remove and discard parchment rounds. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1371\')" class="alarm-clock-icon"><div id="circular-timer-1371" class="circular-timer-container"><script>new CircularTimer("circular-timer-1371", 900)</script></li></ol>' ] );
ingredients_array.push( [ 'CostanzoRecipes_Desserts_CoconutLayerCake', '</div>  <!-- <h3> --><h3 onclick="toggleSection(\'1140\')">Ingredients<span id="heading-ellipses-1140"></h3><div id=\'heading-content-1140\'><p><ul><li onclick="handleItemClick()">4 egg whites  </li><li onclick="handleItemClick()">1 cup sugar  </li><li onclick="handleItemClick()">⅛ tsp. salt  </li><li onclick="handleItemClick()">1 tsp. GOYA® Vanilla Extract  </li><li onclick="handleItemClick()">3 cups unsweetened flaked coconut  </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Desserts_CoconutLayerCake', '</div>  <!-- <h3> --><h3 onclick="toggleSection(\'1141\')">Method:<span id="heading-ellipses-1141"></h3><div id=\'heading-content-1141\'><p><ol><li onclick="handleItemClick()">Meanwhile, in heatproof bowl, whisk together egg whites, sugar and salt. Set bowl over saucepan containing about 1" simmering water (making sure bottom of bowl does not touch water). Whisk constantly until mixture is opaque and warm to the touch (about 120 degreesF on quick-read thermometer), about 3 minutes. Remove bowl from heat. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1372\')" class="alarm-clock-icon"><div id="circular-timer-1372" class="circular-timer-container"><script>new CircularTimer("circular-timer-1372", 180)</script></li><li onclick="handleItemClick()">Using electric mixer, beat egg whites on high speed until glossy white and sticky and mixture cools to about 80 degreesF on quick-read thermometer, about 4 minutes. Reduce speed to medium and beat in vanilla; set aside. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1373\')" class="alarm-clock-icon"><div id="circular-timer-1373" class="circular-timer-container"><script>new CircularTimer("circular-timer-1373", 240)</script></li></ol>' ] );
methods_array.push( [ 'CostanzoRecipes_Desserts_CoconutLayerCake', '</div>  <!-- <h3> --><h3 onclick="toggleSection(\'1142\')">Method:<span id="heading-ellipses-1142"></h3><div id=\'heading-content-1142\'><p><ol><li onclick="handleItemClick()">Place one cooled cake round (top side-up) on cake plate. </li><li onclick="handleItemClick()">Using off-set metal spatula, evenly spread with ½ cup frosting; sprinkle with ¾ cup flaked coconut. </li><li onclick="handleItemClick()">Stack second cake layer (top side-up) on frosted cake. </li><li onclick="handleItemClick()">Evenly spread cake with another ½ cup frosting; sprinkle with ¾ cup flaked coconut. </li><li onclick="handleItemClick()">Stack last cake layer (top side-up) on frosted cake. </li><li onclick="handleItemClick()">Evenly spread remaining frosting on top and sides of cake. </li><li onclick="handleItemClick()">Sprinkle top of cake with about ¾ cup flaked coconut. </li><li onclick="handleItemClick()">Using hands, press remaining coconut on sides of cake. </li></ol>' ] );
credits_array.push( [ 'CostanzoRecipes_Desserts_CoconutLayerCake', '<p>A Fantastic, Moist and Fluffy Coconut Cake Recipe <p>Fluffy layers of coconut cake, sweet, sticky 7-minute frosting and flakes of unsweetened coconut are piled high to create a dramatic, delicious Coconut Layer Cake that looks beautiful and tastes spectacular.  <p>Prep time: 10 min. <p>Total time: 1 hr., plus cooling time <p>Serves 8 <p><a href="http://www.goya.com/english/recipes/print_recipe.html?recipeID=579&langID=0" target="_blank">http://www.goya.com/english/recipes/print_recipe.html?recipeID=579&langID=0</a> <p>[2016-09-01 Thu] Collected this recipe, but I haven\'t made it yet. ' ] );
ingredients_array.push( [ 'CostanzoRecipes_Desserts_ClassicPieDough', '<p><ul><li onclick="handleItemClick()">1¼ cups (150g | 6.25 ounces) all-purpose flour </li><li onclick="handleItemClick()">1 Tbsp. sugar </li><li onclick="handleItemClick()">½ tsp. salt </li><li onclick="handleItemClick()">4 Tbsp. (23g) vegetable shortening, cut into ½-inch pieces and chilled </li><li onclick="handleItemClick()">6 Tbsp. unsalted butter, cut into ¼-inch pieces and chilled </li><li onclick="handleItemClick()">About 3-4 tablespoons ice water, plus more as needed </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Desserts_ClassicPieDough', '<p><ol><li onclick="handleItemClick()">Process flour, sugar and salt in food processor until combined, about 5 seconds. Scatter shortening over top and process until mixture resembles coarse cornmeal, about 10 seconds. Scatter butter over top and pulse until mixture resembles coarse crumbs, about 10 pulses. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1367\')" class="alarm-clock-icon"><div id="circular-timer-1367" class="circular-timer-container"><script>new CircularTimer("circular-timer-1367", 5)</script><script>new CircularTimer("circular-timer-1367", 10)</script></li><li onclick="handleItemClick()">Transfer mixture to a large bowl. Sprinkle 3 tablespoons of ice water over mixture. Stir and press dough together, using stiff rubber spatula, until dough sticks together. If dough does not come together, stir in remaining ice water, ½ tablespoon at a time, until it does. </li><li onclick="handleItemClick()">Divide dough into 2 even pieces. Turn each piece of dough onto a sheet of plastic wrap and flatten each into 4-inch disk. Wrap each piece tightly in plastic and refrigerate for 1 hour. Before rolling out dough, let it sit on counter to soften slightly, about 10 minutes. (Dough can be wrapped tightly in plastic and refrigerated for up to 2 days or frozen for up to 1 month. If frozen, let dough thaw completely on counter before rolling it out.) <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1368\')" class="alarm-clock-icon"><div id="circular-timer-1368" class="circular-timer-container"><script>new CircularTimer("circular-timer-1368", 3600)</script><script>new CircularTimer("circular-timer-1368", 600)</script></li></ol>' ] );
