ingredients_array.push( [ 'CostanzoRecipes_Desserts_ChocolateZucchiniCake', '<p><ul><li onclick="handleItemClick()">½ cup (1 stick) unsalted butter, room temperature </li><li onclick="handleItemClick()">½ cup vegetable oil </li><li onclick="handleItemClick()">1¾ cups sugar </li><li onclick="handleItemClick()">2 large eggs </li><li onclick="handleItemClick()">1 teaspoon vanilla extract </li><li onclick="handleItemClick()">½ cup buttermilk </li><li onclick="handleItemClick()">2 cups grated unpeeled zucchini (about 2 1/2 medium) </li><li onclick="handleItemClick()">2½ cups all purpose flour </li><li onclick="handleItemClick()">½ teaspoon baking powder </li><li onclick="handleItemClick()">4 tablespoons unsweetened cocoa powder </li><li onclick="handleItemClick()">1 teaspoon baking soda </li><li onclick="handleItemClick()">½ teaspoon cinnamon </li><li onclick="handleItemClick()">1 cup semisweet chocolate chips </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Desserts_ChocolateZucchiniCake', '<p><ol><li onclick="handleItemClick()">Preheat oven to 350 degrees. </li><li onclick="handleItemClick()">Cream together the butter, oil and sugar. Stir in the eggs, vanilla and buttermilk. Mix in the zucchini. </li><li onclick="handleItemClick()">Add the dry ingredients and mix well; fold in the chocolate chips. </li><li onclick="handleItemClick()">Pour into 9x12 inch baking pan. Bake for 40-50 minutes, until tester inserted into center comes out clean. (Mini loaf pan baked for 35(?) minutes.) <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1358\')" class="alarm-clock-icon"><div id="circular-timer-1358" class="circular-timer-container"><script>new CircularTimer("circular-timer-1358", 2400)</script><script>new CircularTimer("circular-timer-1358", 3000)</script></li><li onclick="handleItemClick()">Serve cool and dusted with confectioner\'s sugar. Of course the cake doesn\'t complain if it is served with fresh whipped cream or ice cream! </li></ol>' ] );
