ingredients_array.push( [ 'CostanzoRecipes_Desserts_ChocolateTruffleCookies', '<p><ul><li onclick="handleItemClick()">4 squares (1 oz. each) unsweetened chocolate </li><li onclick="handleItemClick()">2 cups (12 oz.) semi-sweet chocolate chips, divided </li><li onclick="handleItemClick()">⅓ cup butter </li><li onclick="handleItemClick()">1 cup sugar </li><li onclick="handleItemClick()">3 eggs </li><li onclick="handleItemClick()">1½ teaspoons vanilla extract </li><li onclick="handleItemClick()">½ cup all-purpose flour </li><li onclick="handleItemClick()">2 tablespoons baking cocoa </li><li onclick="handleItemClick()">¼ teaspoon baking powder </li><li onclick="handleItemClick()">¼teaspoon salt </li><li onclick="handleItemClick()">Confectioners Sugar </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Desserts_ChocolateTruffleCookies', '<p><ol><li onclick="handleItemClick()">In a microwave or double boiler, melt unsweetened chocolate, 1 cup of chocolate chips and butter. Cool for 10 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1354\')" class="alarm-clock-icon"><div id="circular-timer-1354" class="circular-timer-container"><script>new CircularTimer("circular-timer-1354", 600)</script></li><li onclick="handleItemClick()">In a mixing bowl, beat sugar and eggs for 2 minutes until well blended. Beat in vanilla and the chocolate mixture. Mix in flour, cocoa and baking powder. Stir in remaining chocolate chips. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1355\')" class="alarm-clock-icon"><div id="circular-timer-1355" class="circular-timer-container"><script>new CircularTimer("circular-timer-1355", 120)</script></li><li onclick="handleItemClick()">Cover and chll for at least 3 hours. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1356\')" class="alarm-clock-icon"><div id="circular-timer-1356" class="circular-timer-container"><script>new CircularTimer("circular-timer-1356", 10800)</script></li><li onclick="handleItemClick()">Prehat oven to 350 degrees. With lightly floured hands, roll into 1-inch balls. Place on ungreased cookie sheets. Bake at 350 degrees for 10-12 minutes or until lightly puffed and set. Cool on pan 3-4 minutes before removing to wire rack to cool completely. Dust with confectioners sugar. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1357\')" class="alarm-clock-icon"><div id="circular-timer-1357" class="circular-timer-container"><script>new CircularTimer("circular-timer-1357", 600)</script><script>new CircularTimer("circular-timer-1357", 720)</script><script>new CircularTimer("circular-timer-1357", 180)</script><script>new CircularTimer("circular-timer-1357", 240)</script></li></ol>' ] );
