ingredients_array.push( [ 'CostanzoRecipes_Desserts_ChocolateCakePanCakeKingArthurFloursOriginal', '<h3 onclick="toggleSection(\'1117\')">Cake (sourdough-based, single recipe)<span id="heading-ellipses-1117"></h3><div id=\'heading-content-1117\'><p><ul><li onclick="handleItemClick()">¾ cup (88.5g) King Arthur Unbleached All-Purpose Flour or Gluten-Free Measure for Measure Flour </li><li onclick="handleItemClick()">1 cup (198g) sugar </li><li onclick="handleItemClick()">¼ cup (21g) Dutch-process cocoa, natural cocoa, or Triple Cocoa Blend </li><li onclick="handleItemClick()">½ teaspoon salt </li><li onclick="handleItemClick()">½ teaspoon espresso powder, optional </li><li onclick="handleItemClick()">1 teaspoon baking soda </li><li onclick="handleItemClick()">1 cup (~242g) sourdough starter </li><li onclick="handleItemClick()">1 teaspoon vanilla extract, gluten-free if necessary </li><li onclick="handleItemClick()">1 tablespoon (14g) vinegar, cider or white </li><li onclick="handleItemClick()">⅓ cup (67g) vegetable oil </li><li onclick="handleItemClick()">½ cup (113.5g) cold water </li></ul></div>  <!-- <h3> --><h3 onclick="toggleSection(\'1118\')">Cake (sourdough-based, double recipe)<span id="heading-ellipses-1118"></h3><div id=\'heading-content-1118\'><p><ul><li onclick="handleItemClick()">1½ cup (177g) King Arthur Unbleached All-Purpose Flour or Gluten-Free Measure for Measure Flour </li><li onclick="handleItemClick()">2 cup (396g) sugar </li><li onclick="handleItemClick()">½ cup (42g) Dutch-process cocoa, natural cocoa, or Triple Cocoa Blend </li><li onclick="handleItemClick()">1 teaspoon salt </li><li onclick="handleItemClick()">1 teaspoon espresso powder, optional </li><li onclick="handleItemClick()">2 teaspoons baking soda </li><li onclick="handleItemClick()">2 cup (~848g) sourdough starter </li><li onclick="handleItemClick()">2 teaspoon vanilla extract, gluten-free if necessary </li><li onclick="handleItemClick()">2 tablespoon (28g) vinegar, cider or white </li><li onclick="handleItemClick()">⅔ cup (134g) vegetable oil </li><li onclick="handleItemClick()">1 cup (227g) cold water </li></ul></div>  <!-- <h3> --><h3 onclick="toggleSection(\'1119\')">Cake (sourdough-based, one-and-a-half recipe)<span id="heading-ellipses-1119"></h3><div id=\'heading-content-1119\'><p><ul><li onclick="handleItemClick()">1⅛ cup (133g) King Arthur Unbleached All-Purpose Flour or Gluten-Free Measure for Measure Flour </li><li onclick="handleItemClick()">1½ cup (297g) sugar </li><li onclick="handleItemClick()">⅜ cup (31.5g) Dutch-process cocoa, natural cocoa, or Triple Cocoa Blend </li><li onclick="handleItemClick()">¾ teaspoon salt </li><li onclick="handleItemClick()">¾ teaspoon espresso powder, optional </li><li onclick="handleItemClick()">1½ teaspoon baking soda </li><li onclick="handleItemClick()">1½ cup (~363g) sourdough starter </li><li onclick="handleItemClick()">1½ teaspoon vanilla extract, gluten-free if necessary </li><li onclick="handleItemClick()">1½ tablespoon (21g) vinegar, cider or white </li><li onclick="handleItemClick()">½ cup (100g) vegetable oil </li><li onclick="handleItemClick()">¾ cup (170g) cold water </li></ul></div>  <!-- <h3> --><h3 onclick="toggleSection(\'1120\')">Cake (normal)<span id="heading-ellipses-1120"></h3><div id=\'heading-content-1120\'><p><ul><li onclick="handleItemClick()">1½ cups (177g) King Arthur Unbleached All-Purpose Flour or Gluten-Free Measure for Measure Flour </li><li onclick="handleItemClick()">1 cup (198g) sugar </li><li onclick="handleItemClick()">¼ cup (21g) Dutch-process cocoa, natural cocoa, or Triple Cocoa Blend </li><li onclick="handleItemClick()">½ teaspoon salt </li><li onclick="handleItemClick()">½ teaspoon espresso powder, optional </li><li onclick="handleItemClick()">1 teaspoon baking soda </li><li onclick="handleItemClick()">1 teaspoon vanilla extract, gluten-free if necessary </li><li onclick="handleItemClick()">1 tablespoon (14g) vinegar, cider or white </li><li onclick="handleItemClick()">⅓ cup (67g) vegetable oil </li><li onclick="handleItemClick()">1 cup (227g) cold water </li></ul></div>  <!-- <h3> --><h3 onclick="toggleSection(\'1121\')">Icing<span id="heading-ellipses-1121"></h3><div id=\'heading-content-1121\'><p><ul><li onclick="handleItemClick()">1½ cups (255g) semisweet chocolate chips </li><li onclick="handleItemClick()">½ cup (113g) half-and-half  -<i class="conditional-space-after">or-</i>⅓ cup cold brewed coffee </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Desserts_ChocolateCakePanCakeKingArthurFloursOriginal', '<p><ol><li onclick="handleItemClick()">Preheat your oven to 350°F. </li><li onclick="handleItemClick()">Lightly grease an 8" square or 9" round pan that\'s at least 2" deep. </li><li onclick="handleItemClick()">Whisk the dry ingredients together in a medium-sized bowl. </li><li onclick="handleItemClick()">Whisk the vanilla, vinegar, vegetable oil, and water in a separate bowl. </li><li onclick="handleItemClick()">Pour the wet ingredients into the bowl of dry ingredients, stirring until thoroughly combined. </li><li onclick="handleItemClick()">Pour the batter into the prepared pan. </li><li onclick="handleItemClick()">Bake the cake for 30 to 35 minutes, until a toothpick inserted into the center comes out clean, or with a few moist crumbs clinging to it. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1345\')" class="alarm-clock-icon"><div id="circular-timer-1345" class="circular-timer-container"><script>new CircularTimer("circular-timer-1345", 1800)</script><script>new CircularTimer("circular-timer-1345", 2100)</script></li><li onclick="handleItemClick()">Serve the cake right from the pan; warm from the oven, it\'s wonderful with a big glass of milk. </li><li onclick="handleItemClick()">Or, once cool, frost the cake with this simple chocolate frosting: <ol><li onclick="handleItemClick()">Heat the chocolate chips with the half & half until the chips melt. </li><li onclick="handleItemClick()">Stir until smooth, and pour/spread over the cake. </li><li onclick="handleItemClick()">For a non-dairy icing, substitute ⅓ cup cold brewed coffee (or water) for the half-and-half. </li></ol></li></ol><p><div class="tip">Store cake, well covered, at room temperature for several days; freeze for longer storage. </div>' ] );
yields_array.push( [ 'CostanzoRecipes_Desserts_ChocolateCakePanCakeKingArthurFloursOriginal', '<p>one 8" square or 9" round cake ' ] );
