ingredients_array.push( [ 'CostanzoRecipes_Desserts_BreadPudding', '<p><ul><li onclick="handleItemClick()">2 eggs </li><li onclick="handleItemClick()">2 cups hot milk </li><li onclick="handleItemClick()">½ cup brown sugar </li><li onclick="handleItemClick()">¼ teasp salt </li><li onclick="handleItemClick()">½ teasp cinnamon </li><li onclick="handleItemClick()">½ teasp vanilla </li><li onclick="handleItemClick()">½ cup raisins </li><li onclick="handleItemClick()">1 Tblspn butter </li><li onclick="handleItemClick()">3 slices bread, cubed </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Desserts_BreadPudding', '<p><ol><li onclick="handleItemClick()">Heat oven to 375 degrees. </li><li onclick="handleItemClick()">In a large bowl, beat eggs. Mix in milk,sugar,salt, cinnamon, vanilla and raisins. </li><li onclick="handleItemClick()">Melt butter and spread around casserole. Add cubed bread. Pour liquid mixture over bread. Bake until brown, at least 40 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1341\')" class="alarm-clock-icon"><div id="circular-timer-1341" class="circular-timer-container"><script>new CircularTimer("circular-timer-1341", 2400)</script></li></ol>' ] );
