ingredients_array.push( [ 'CostanzoRecipes_Desserts_BlueberrySauce', '<p><ul><li onclick="handleItemClick()">½ cups water </li><li onclick="handleItemClick()">½ cup sugar (<i class="conditional-space-after">try a <b class="conditional-space-after">¼ cup</b>?</i>) </li><li onclick="handleItemClick()">2 tablespoons fresh lemon juice </li><li onclick="handleItemClick()">2 teaspoons cornstarch </li><li onclick="handleItemClick()">4 pints fresh blueberries </li><li onclick="handleItemClick()">¼ cup red currant (or similar) jelly </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Desserts_BlueberrySauce', '<p><ol><li onclick="handleItemClick()">Bring first 4 ingredients to a boil in a large saucepan, stirring constantly to dissolve sugar and cornstarch. </li><li onclick="handleItemClick()">Add blueberries and jelly. </li><li onclick="handleItemClick()">Bring to boil. </li><li onclick="handleItemClick()">Reduce heat to medium-low; simmer until mixture is slightly thickened and reduced, stirring frequently, about 30 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1336\')" class="alarm-clock-icon"><div id="circular-timer-1336" class="circular-timer-container"><script>new CircularTimer("circular-timer-1336", 1800)</script></li><li onclick="handleItemClick()">Serve warm or at room temperature. </li></ol><p><div class="tip">May be made 4 days ahead. Cover and chill. Rewarm sauce before serving, if desired. </div><p><div class="tip">May also be frozen. </div>' ] );
