ingredients_array.push( [ 'CostanzoRecipes_Desserts_BlueberryCobbler', '<p><ul><li onclick="handleItemClick()">⅔ cup all-purpose flour </li><li onclick="handleItemClick()">½ cup sugar </li><li onclick="handleItemClick()">1½ teasp baking powder </li><li onclick="handleItemClick()">¼ teasp salt, if desired </li><li onclick="handleItemClick()">⅔ cup skim milk </li><li onclick="handleItemClick()">2 Tbls butter or margarine, melted </li><li onclick="handleItemClick()">2 cups blueberries (clean & wash) </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Desserts_BlueberryCobbler', '<p><ol><li onclick="handleItemClick()">In a medium bowl, combine the flour, sugar, baking powder and salt. </li><li onclick="handleItemClick()">Stir in the milk and mix the batter until it is smooth. </li><li onclick="handleItemClick()">Pour the melted butter or margarine into a 1- or 1½-quart casserole-type baking dish. </li><li onclick="handleItemClick()">Pour in the batter, sprinkle the blueberries on top. </li><li onclick="handleItemClick()">Bake the cobbler in a preheated 350° oven for 40 to 45 minutes or until it is lightly browned. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1327\')" class="alarm-clock-icon"><div id="circular-timer-1327" class="circular-timer-container"><script>new CircularTimer("circular-timer-1327", 2400)</script><script>new CircularTimer("circular-timer-1327", 2700)</script></li><li onclick="handleItemClick()">Spoon out the cobbler onto individual dishes to serve. </li><li onclick="handleItemClick()">Makes 5 servings. </li></ol>' ] );
