ingredients_array.push( [ 'CostanzoRecipes_Desserts_Biscotti', '<p><ul><li onclick="handleItemClick()">3 cup all purpose flour </li><li onclick="handleItemClick()">2 cup white granulated sugar </li><li onclick="handleItemClick()">1½ tsp baking powder </li><li onclick="handleItemClick()">salt </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Desserts_Biscotti', '<p><ol><li onclick="handleItemClick()">Preheat oven to 350 degrees. </li><li onclick="handleItemClick()">Combine ingredients with hands.  </li><li onclick="handleItemClick()">Add 1 cup toasted whole almonds. Add 3-4 eggs (8 ounces). </li><li onclick="handleItemClick()">Roll dough in sugar, bake 45-60 minutes. Take out, slice, back in oven 5-10 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1326\')" class="alarm-clock-icon"><div id="circular-timer-1326" class="circular-timer-container"><script>new CircularTimer("circular-timer-1326", 2700)</script><script>new CircularTimer("circular-timer-1326", 3600)</script><script>new CircularTimer("circular-timer-1326", 300)</script><script>new CircularTimer("circular-timer-1326", 600)</script></li></ol><h3 onclick="toggleSection(\'1109\')">Variations:<span id="heading-ellipses-1109"></h3><div id=\'heading-content-1109\'><p><ul><li onclick="handleItemClick()">Dried figs + candied lemon peel (and ground cardamom) </li><li onclick="handleItemClick()">Cocoa + coffee extract + choc chips </li></ul>' ] );
