ingredients_array.push( [ 'CostanzoRecipes_Desserts_BananaBread', '<p><ul><li onclick="handleItemClick()">1 cup all-purpose flour </li><li onclick="handleItemClick()">1 cup whole wheat flour </li><li onclick="handleItemClick()">1 Tbsp baking powder </li><li onclick="handleItemClick()">1 tsp baking soda </li><li onclick="handleItemClick()">1 tsp salt </li><li onclick="handleItemClick()">½ cup (1 stick) butter </li><li onclick="handleItemClick()">2 eggs </li><li onclick="handleItemClick()">⅓ cup honey </li><li onclick="handleItemClick()">1¼ cups mashed ripe bananas (3-4) </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Desserts_BananaBread', '<p><ol><li onclick="handleItemClick()">Preheat oven to 350 degrees F. Grease and flour a 9 x 5 x 2¼-inch loaf pan. </li><li onclick="handleItemClick()">In a medium bowl or on a piece of waxed paper, stir together both flours, the baking powder, baking soda and salt with a whisk. </li><li onclick="handleItemClick()">In a large bowl, cream the butter and the sugar until light and fluffy. Beat in the eggs until thoroughly combined. Beat in the honey. </li><li onclick="handleItemClick()">Beat in the banana alternately with the flour mixture. Spoon into prepared pan. Bake 50 minutes or until a wooden pick inserted in the center comes out clean. Remove from the oven an turn onto a wire rack to cool. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1325\')" class="alarm-clock-icon"><div id="circular-timer-1325" class="circular-timer-container"><script>new CircularTimer("circular-timer-1325", 3000)</script></li><li onclick="handleItemClick()">Optional: Add 1 cup chopped nuts to the batter. </li></ol>' ] );
