ingredients_array.push( [ 'CostanzoRecipes_Desserts_BananaBreadvegan', '<p><ul><li onclick="handleItemClick()">1 cup applesauce </li><li onclick="handleItemClick()">1/2 cup turbinado sugar or raw sugar (I’ve used agave with a good result also) </li><li onclick="handleItemClick()">3 extra ripe bananas, mashed </li><li onclick="handleItemClick()">1 Tbsp plant milk </li><li onclick="handleItemClick()">1 tsp vanilla </li><li onclick="handleItemClick()">1 tsp baking soda </li><li onclick="handleItemClick()">1/4 tsp salt </li><li onclick="handleItemClick()">2 cups whole wheat pastry flour </li><li onclick="handleItemClick()">1/2 cup vegan chocolate chips (optional) </li><li onclick="handleItemClick()">1/2 cup nuts (optional) </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Desserts_BananaBreadvegan', '<p><ol><li onclick="handleItemClick()">Preheat oven to 350°. </li><li onclick="handleItemClick()">In a large mixing bowl add 1 cup applesauce and 1/2 cup turbinado sugar. Mix with a wooden spoon. </li><li onclick="handleItemClick()">Add the bananas, plant milk and vanilla then mix well. </li><li onclick="handleItemClick()">Add baking soda, salt, and flour. Mix again. </li><li onclick="handleItemClick()">Add optional nuts and/or chocolate chips. </li><li onclick="handleItemClick()">Pour into a sprayed pan, I use a bundt pan for best results. </li><li onclick="handleItemClick()">Cook for 40 minutes until a toothpick comes out clean. Using a loaf pan will require a longer baking time. When making muffins with this recipe they are usually done in 15 to 20 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1324\')" class="alarm-clock-icon"><div id="circular-timer-1324" class="circular-timer-container"><script>new CircularTimer("circular-timer-1324", 2400)</script><script>new CircularTimer("circular-timer-1324", 900)</script><script>new CircularTimer("circular-timer-1324", 1200)</script></li><li onclick="handleItemClick()">Remove from pan once cool. </li></ol><p><div class="tip">Serve this to for a tasty dessert, snack, or even with a cup of coffee. If possible wait until the next day to slice this and it will taste like heaven! We have even taken this on a road trip for a sweet snack. </div><p><div class="note">We love to make this in a bundt pan, wrap it up once cool and wait until the next day to cut it. It\'s moist with a perfect crumb. </div><div class="note">If you can\'t wait it will still be good. Just store in an air tight container at room temperature. If it\'s exceptionally hot, store in the fridge. </div>' ] );
credits_array.push( [ 'CostanzoRecipes_Desserts_BananaBreadvegan', '<p>IsVegan <p><ul><li onclick="handleItemClick()">From Patsy McCutcheon, <a href="https://myplantbasedfamily.com/2012/10/17/the-worlds-best-banana-bread/" target="_blank">https://myplantbasedfamily.com/2012/10/17/the-worlds-best-banana-bread/</a> </li><li onclick="handleItemClick()">date created: [2022-07-04 Mon]  </li><li onclick="handleItemClick()">Patsy made this for the July 2022 Pickleball Tournament. Hers used oat(?) flour and was yummy! </li></ul>' ] );
ingredients_array.push( [ 'CostanzoRecipes_Desserts_AuntAnnasAppleCake', '<p><ul><li onclick="handleItemClick()">4 cups cut up apples (sprinkle with sugar, let stand) </li><li onclick="handleItemClick()">1 cup sugar </li><li onclick="handleItemClick()">2 cups flour </li><li onclick="handleItemClick()">2 eggs, slightly beaten </li><li onclick="handleItemClick()">¾ cup oil </li><li onclick="handleItemClick()">1½ teaspoon baking soda </li><li onclick="handleItemClick()">2 teaspoon vanilla </li><li onclick="handleItemClick()">1 teaspoon salt </li><li onclick="handleItemClick()">1 teaspoon cinnamon </li><li onclick="handleItemClick()">nuts and/or raisins </li></ul>' ] );
