ingredients_array.push( [ 'CostanzoRecipes_Desserts_AppleFritters', '<p><ul><li onclick="handleItemClick()">Points: 4 </li><li onclick="handleItemClick()">Servings: 4 </li><li onclick="handleItemClick()">Preparation Time :12 minutes <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1315\')" class="alarm-clock-icon"><div id="circular-timer-1315" class="circular-timer-container"><script>new CircularTimer("circular-timer-1315", 720)</script></li><li onclick="handleItemClick()">Cooking Time: 6 minutes <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1316\')" class="alarm-clock-icon"><div id="circular-timer-1316" class="circular-timer-container"><script>new CircularTimer("circular-timer-1316", 360)</script></li><li onclick="handleItemClick()">Level of Difficulty: Easy </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Desserts_AppleFritters', '<p><ol><li onclick="handleItemClick()">½ cup all-purpose flour  </li><li onclick="handleItemClick()">2 Tbsp sugar  </li><li onclick="handleItemClick()">⅛ tsp table salt  </li><li onclick="handleItemClick()">½ cup fat-free egg substitute  </li><li onclick="handleItemClick()">¼ cup fat-free skim milk  </li><li onclick="handleItemClick()">½ tsp vanilla extract </li><li onclick="handleItemClick()">2 pinches Apple Pie spice </li><li onclick="handleItemClick()">2 large apple(s), peeled, cored and coarsely grated (McIntosh or Granny Smith recommended)  </li><li onclick="handleItemClick()">1 Tbsp reduced-calorie margarine  </li><li onclick="handleItemClick()">1 Tbsp powdered sugar  </li><li onclick="handleItemClick()">Instructions:  </li><li onclick="handleItemClick()">In a large bowl, combine flour, granulated sugar and salt; mix well. Whisk in egg substitute, milk and extract; stir in apples.  </li><li onclick="handleItemClick()">Melt margarine in a large nonstick skillet over medium-high heat. Drop apple mixture into pan, making 8 even fritters. Cook until golden brown, about 2 to 3 minutes per side.  <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1317\')" class="alarm-clock-icon"><div id="circular-timer-1317" class="circular-timer-container"><script>new CircularTimer("circular-timer-1317", 120)</script><script>new CircularTimer("circular-timer-1317", 180)</script></li><li onclick="handleItemClick()">Sift powdered sugar over top of fritters just before serving.  </li></ol>' ] );
