ingredients_array.push( [ 'CostanzoRecipes_Desserts_AppleCrisp', '<h3 onclick="toggleSection(\'1105\')">Base<span id="heading-ellipses-1105"></h3><div id=\'heading-content-1105\'><p><ul><li onclick="handleItemClick()">4 cups (about 4 medium) apples peeled, cored, cut into ¼-inch slices </li><li onclick="handleItemClick()">⅔ to ¾ cup packed brown sugar </li><li onclick="handleItemClick()">½ cup all-purpose flour </li><li onclick="handleItemClick()">½ cup oats </li><li onclick="handleItemClick()">¾ teaspoon ground cinnamon </li><li onclick="handleItemClick()">¾ teaspoon ground nutmeg </li><li onclick="handleItemClick()">⅓ cup butter </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Desserts_AppleCrisp', '<p><ol><li onclick="handleItemClick()">Preheat oven to 450 degrees. </li><li onclick="handleItemClick()">Arrange apples in a greased square pan 8x8x2 inches. </li><li onclick="handleItemClick()">Mix remaining ingredients and sprinkle over apples. </li><li onclick="handleItemClick()">Bake until topping is golden brown and apples are tender, about 30 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1314\')" class="alarm-clock-icon"><div id="circular-timer-1314" class="circular-timer-container"><script>new CircularTimer("circular-timer-1314", 1800)</script></li><li onclick="handleItemClick()">Serve warm and, if desired, with cream or ice cream. </li></ol>' ] );
