ingredients_array.push( [ 'CostanzoRecipes_Breakfast_OvernightOats', '<p><ul><li onclick="handleItemClick()">2 cups (200 g) old-fashioned rolled oats </li><li onclick="handleItemClick()">2 cups (524 g) dairy milk or unsweetened non-dairy milk </li><li onclick="handleItemClick()">1 cup (285 g) plain Greek or non-dairy yogurt </li><li onclick="handleItemClick()">3 (60 g) tablespoons maple syrup or honey </li><li onclick="handleItemClick()">1 tablespoon chia seeds (optional) </li><li onclick="handleItemClick()">¼ teaspoon ground cinnamon </li><li onclick="handleItemClick()">¼ teaspoon kosher salt </li><li onclick="handleItemClick()">Optional toppings: fruits, nuts, seeds, nut butter </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Breakfast_OvernightOats', '<p><ol><li onclick="handleItemClick()">Place all the ingredients in a large bowl. </li><li onclick="handleItemClick()">Stir together until well combined. </li><li onclick="handleItemClick()">Cover and refrigerate overnight. You can divide the oats into individual jars at this point if desired. Cover and refrigerate for at least 4 hours, preferably overnight. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1288\')" class="alarm-clock-icon"><div id="circular-timer-1288" class="circular-timer-container"><script>new CircularTimer("circular-timer-1288", 14400)</script></li><li onclick="handleItemClick()">Stir again before serving with desired toppings. </li></ol>' ] );
