ingredients_array.push( [ 'CostanzoRecipes_Breakfast_OatBranMuffinstxt', '<p>Gourmet | January 2011 by Ian Knauer <p><ul><li onclick="handleItemClick()">active time: 20 min </li><li onclick="handleItemClick()">total time:	 40 min </li><li onclick="handleItemClick()">Equipment: a muffin pan with 12 (½-cup) muffin cups </li></ul><p><ul><li onclick="handleItemClick()">2 cups all-purpose flour </li><li onclick="handleItemClick()">¾ cup oat bran </li><li onclick="handleItemClick()">¼ cup sugar </li><li onclick="handleItemClick()">2 teaspoons baking powder </li><li onclick="handleItemClick()">¾ teaspoons baking soda </li><li onclick="handleItemClick()">1 teaspoon salt </li><li onclick="handleItemClick()">½ teaspoon cinnamon </li><li onclick="handleItemClick()">1 cup well-shaken buttermilk </li><li onclick="handleItemClick()">⅓ cup mild honey </li><li onclick="handleItemClick()">½ stick unsalted butter, melted and cooled </li><li onclick="handleItemClick()">2 large eggs </li><li onclick="handleItemClick()">1 cup golden raisins </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Breakfast_OatBranMuffinstxt', '<p><ol><li onclick="handleItemClick()">Preheat oven to 375 degrees F with rack in middle. Butter muffin pan. </li><li onclick="handleItemClick()">Whisk together flour, oat bran, sugar, baking powder, baking soda, salt, and cinnamon in a large bowl. </li><li onclick="handleItemClick()">In a separate bowl, whisk together buttermilk, honey, butter, and eggs. Add to dry ingredients along with raisins and stir until just combined. </li><li onclick="handleItemClick()">Divide batter among muffin cups. Bake until a wooden pick inserted into center of a muffin comes out clean, about 20 minutes. Turn out onto a rack to cool. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1287\')" class="alarm-clock-icon"><div id="circular-timer-1287" class="circular-timer-container"><script>new CircularTimer("circular-timer-1287", 1200)</script></li><li onclick="handleItemClick()">Cooks\' note: Muffins keep in an airtight container at room temperature 3 days. </li></ol>' ] );
