ingredients_array.push( [ 'CostanzoRecipes_Breakfast_GreatGranola', '<p><ul><li onclick="handleItemClick()">¼ cup butter </li><li onclick="handleItemClick()">¼ cup (85 grams) honey </li><li onclick="handleItemClick()">3 cup rolled oats </li><li onclick="handleItemClick()">1 cup sunflower seeds </li><li onclick="handleItemClick()">1½ teaspoon cinnamon </li><li onclick="handleItemClick()">1 cup nuts (almonds are nice) </li><li onclick="handleItemClick()">1 cup shredded and/or flaked coconut </li><li onclick="handleItemClick()">½ cup wheat germ and/or flax seed </li><li onclick="handleItemClick()">⅔ cup raisins </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Breakfast_GreatGranola', '<p><ol><li onclick="handleItemClick()">Preheat oven to 350 degrees. </li><li onclick="handleItemClick()">In a large oven-proof 9x13 inch baking pan, melt butter and stir in honey. </li><li onclick="handleItemClick()">Add the oats, seeds, cinnamon and nuts. Bake for 15 minutes, stirring occasionally. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1272\')" class="alarm-clock-icon"><div id="circular-timer-1272" class="circular-timer-container"><script>new CircularTimer("circular-timer-1272", 900)</script></li><li onclick="handleItemClick()">Add coconut, wheat germ and/or flax seed. Bake for 10 minutes longer, or until the mixture is lightly browned. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1273\')" class="alarm-clock-icon"><div id="circular-timer-1273" class="circular-timer-container"><script>new CircularTimer("circular-timer-1273", 600)</script></li><li onclick="handleItemClick()">Remove granola from the oven, stir in raisins and cool granola completely before transferring to a storage container. </li></ol>' ] );
