ingredients_array.push( [ 'CostanzoRecipes_Breakfast_GingerbreadGranola', '<h3 onclick="toggleSection(\'1091\')">Dry<span id="heading-ellipses-1091"></h3><div id=\'heading-content-1091\'><p><ul><li onclick="handleItemClick()">293 g rolled oats (GF for gluten-free eaters) </li><li onclick="handleItemClick()">193 g raw nuts (I used almonds, pecans, and a few walnuts) </li><li onclick="handleItemClick()">37 g organic cane sugar (or sub extra molasses or maple syrup mixed in with wet ingredients) </li><li onclick="handleItemClick()">¼ tsp sea salt </li><li onclick="handleItemClick()">1½ tsp ground cinnamon </li><li onclick="handleItemClick()">¾ tsp ground ginger </li><li onclick="handleItemClick()">1 pinch ground cloves (optional) </li></ul></div>  <!-- <h3> --><h3 onclick="toggleSection(\'1092\')">Wet<span id="heading-ellipses-1092"></h3><div id=\'heading-content-1092\'><p><ul><li onclick="handleItemClick()">60 ml coconut or olive oil </li><li onclick="handleItemClick()">80 ml maple syrup (sub agave, or honey if not vegan) </li><li onclick="handleItemClick()">30 ml molasses </li><li onclick="handleItemClick()">1 tsp vanilla extract (optional) </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Breakfast_GingerbreadGranola', '<p><ol><li onclick="handleItemClick()">Preheat oven to 325 degrees F (162 C). </li><li onclick="handleItemClick()">Mix the dry ingredients together in a large bowl. </li><li onclick="handleItemClick()">In a small saucepan over medium low heat, warm the coconut oil, maple syrup, molasses and vanilla extract. Pour over the dry ingredients and mix well. </li><li onclick="handleItemClick()">Spread the mixture evenly onto a a large baking sheet and bake for 18–22 minutes, stirring near the halfway point to ensure even cooking. The coconut oil will help this granola crisp up nicely, but be sure to watch it carefully as it browns quickly. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1270\')" class="alarm-clock-icon"><div id="circular-timer-1270" class="circular-timer-container"><script>new CircularTimer("circular-timer-1270", 1320)</script></li><li onclick="handleItemClick()">Once the granola is visibly browned (about 19 minutes for me), remove from the oven and let cool completely on the pan before storing. Consider turning the oven off and leaving the pan in it for the granola to get extra crispy. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1271\')" class="alarm-clock-icon"><div id="circular-timer-1271" class="circular-timer-container"><script>new CircularTimer("circular-timer-1271", 1140)</script></li><li onclick="handleItemClick()">Keep in a container that has an air-tight seal and it should keep for a few weeks. </li></ol>' ] );
