ingredients_array.push( [ 'CostanzoRecipes_Breakfast_CranberryMuffins', '<p>Preheat oven to <b class="conditional-space-after">400 degrees</b>. <p><ul><li onclick="handleItemClick()">⅔ cup sugar </li><li onclick="handleItemClick()">1¾ cup flour </li><li onclick="handleItemClick()">1 Tblsp baking powder </li><li onclick="handleItemClick()">dash salt </li><li onclick="handleItemClick()">1 Tblsp grated orange zest </li><li onclick="handleItemClick()">1	egg </li><li onclick="handleItemClick()">1 cup buttermilk </li><li onclick="handleItemClick()">⅓ cup vegetable oil </li><li onclick="handleItemClick()">½ cup rinsed cranberries </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Breakfast_CranberryMuffins', '<p><ol><li onclick="handleItemClick()">Mix together: <ul><li onclick="handleItemClick()">sugar </li><li onclick="handleItemClick()">flour </li><li onclick="handleItemClick()">baking powder </li><li onclick="handleItemClick()">salt </li></ul></li><li onclick="handleItemClick()">In another bowl, whisk together: <ul><li onclick="handleItemClick()">orange zest </li><li onclick="handleItemClick()">egg </li><li onclick="handleItemClick()">buttermilk </li><li onclick="handleItemClick()">vegetable oil </li></ul></li><li onclick="handleItemClick()">Mix cranberries into dry ingredients. Pour into liquid mixture and stir. </li><li onclick="handleItemClick()">Spoon into paper-lined muffin tin about ⅔ full. </li><li onclick="handleItemClick()">Bake at 400 degrees for 25 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1264\')" class="alarm-clock-icon"><div id="circular-timer-1264" class="circular-timer-container"><script>new CircularTimer("circular-timer-1264", 1500)</script></li></ol>' ] );
