ingredients_array.push( [ 'CostanzoRecipes_Breakfast_ButtermilkSyrup', '<p><ul><li onclick="handleItemClick()">½ cup unsalted butter </li><li onclick="handleItemClick()">1 cup sugar </li><li onclick="handleItemClick()">1 cup buttermilk </li><li onclick="handleItemClick()">2 teaspoons vanilla </li><li onclick="handleItemClick()">1 tablespoon Light karo syrup </li><li onclick="handleItemClick()">½ teaspoon baking soda </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Breakfast_ButtermilkSyrup', '<p><ol><li onclick="handleItemClick()">In a large pot or saucepan over medium heat add the butter, sugar, buttermilk, vanilla and karo syrup. Heat until everything is well combined and bring to a boil.   </li><li onclick="handleItemClick()">Quickly remove the pot from the heat and add the baking soda, place back over the heat, stirring constantly. The syrup will grow large and threaten to boil over which is why a large pot is essential. If it gets too close to the edge remove from the burner and continue stirring to bring it down.  </li><li onclick="handleItemClick()">Stir for 30 seconds over the heat (I often just take mine off so I don\'t risk boiling over) and serve immediately.  <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1258\')" class="alarm-clock-icon"><div id="circular-timer-1258" class="circular-timer-container"><script>new CircularTimer("circular-timer-1258", 30)</script></li></ol>' ] );
