ingredients_array.push( [ 'CostanzoRecipes_Breakfast_BlueberryButtermilkMuffins', '<p><ul><li onclick="handleItemClick()">1 cup buttermilk </li><li onclick="handleItemClick()">½ cup butter or margarine -- melted </li><li onclick="handleItemClick()">2 eggs -- beaten </li><li onclick="handleItemClick()">2½ cups all-purpose flour </li><li onclick="handleItemClick()">1 cup sugar </li><li onclick="handleItemClick()">2½ teaspoons baking powder </li><li onclick="handleItemClick()">¼ teaspoon salt </li><li onclick="handleItemClick()">1½ cup Fresh Or Frozen Blueberries </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Breakfast_BlueberryButtermilkMuffins', '<p><ol><li onclick="handleItemClick()">In small bowl, combine buttermilk, butter and eggs until blended. In large bowl, combine flour, sugar, baking powder and salt. Make well in center. Add buttermilk mixture, stirring until flour mixture is just moistened. Fold in blueberries. Spoon batter into greased muffin cups, filling ⅔ full. Bake in preheated 400 degree oven 20 minutes or until tops are golden and wooden pick inserted in center comes out clean. Remove from pan. Serve warm or cool on wire rack. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1254\')" class="alarm-clock-icon"><div id="circular-timer-1254" class="circular-timer-container"><script>new CircularTimer("circular-timer-1254", 1200)</script></li><li onclick="handleItemClick()">This recipe for Blueberry Buttermilk Muffins serves/makes 18 </li></ol>' ] );
