methods_array.push( [ 'CostanzoRecipes_Breakfast_BestButtermilkWaffles', '<p><ol><li onclick="handleItemClick()">Mix dry ingredients. </li><li onclick="handleItemClick()">Add egg yolks to milk and blend well. </li><li onclick="handleItemClick()">Add dry ingredients, mix together. </li><li onclick="handleItemClick()">Add melted butter and beat with a few quick strokes, fold in beaten egg whites. </li><li onclick="handleItemClick()">Bake in waffle iron until golden. </li><li onclick="handleItemClick()">Can add fruits such as blueberries, strawberries, chopped apple and    nuts for variety.  </li></ol>' ] );
