ingredients_array.push( [ 'CostanzoRecipes_Breakfast_BestBranMuffins', '<p><ul><li onclick="handleItemClick()">1 cup whole wheat flour </li><li onclick="handleItemClick()">1 tsp baking soda </li><li onclick="handleItemClick()">½ tsp salt </li><li onclick="handleItemClick()">1½ c. wheat bran </li><li onclick="handleItemClick()">3 Tbls	butter (or oil) </li><li onclick="handleItemClick()">2 Tbls	brown sugar </li><li onclick="handleItemClick()">2 Tbls	molasses </li><li onclick="handleItemClick()">1	egg </li><li onclick="handleItemClick()">1½ c.	buttermilk </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Breakfast_BestBranMuffins', '<p><ol><li onclick="handleItemClick()">Preheat oven to 375 degrees. Grease pan. </li><li onclick="handleItemClick()">Sift flour, soda and salt together. Stir in the bran. </li><li onclick="handleItemClick()">Beat butter or oil and sugar and molasses together. Add egg and buttermilk. Mix dry ingredients into liquids. Place in the pan, about ¾ full. Bake muffins 15 to 20 minutes; or a loaf for about 30 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1253\')" class="alarm-clock-icon"><div id="circular-timer-1253" class="circular-timer-container"><script>new CircularTimer("circular-timer-1253", 900)</script><script>new CircularTimer("circular-timer-1253", 1200)</script><script>new CircularTimer("circular-timer-1253", 1800)</script></li><li onclick="handleItemClick()">Alternative: use a 3" x 7" loaf. Makes a nice tea bread. </li></ol>' ] );
