methods_array.push( [ 'CostanzoRecipes_Breakfast_BelgianWaffles', '<p><ol><li onclick="handleItemClick()">Combine in large bowl: <ul><li onclick="handleItemClick()">2 c.	all purpose flour </li><li onclick="handleItemClick()">2 T.	sugar </li><li onclick="handleItemClick()">¾	t salt </li><li onclick="handleItemClick()">½	t active dry yeast </li></ul></li><li onclick="handleItemClick()">Whisk into dry ingredients: <ul><li onclick="handleItemClick()">½ c.	 unsalted butter, melted </li><li onclick="handleItemClick()">2 c.	 whole milk </li></ul></li><li onclick="handleItemClick()">Cover and let sit overnight, covered on counter. Next morning, add: <ul><li onclick="handleItemClick()">3	eggs, separated </li></ul></li><li onclick="handleItemClick()">Whisk yolks, whip whites to soft peaks and gently fold into batter. </li><li onclick="handleItemClick()">Cook for 3 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1252\')" class="alarm-clock-icon"><div id="circular-timer-1252" class="circular-timer-container"><script>new CircularTimer("circular-timer-1252", 180)</script></li><li onclick="handleItemClick()">Makes 10-12 waffles </li></ol>' ] );
