methods_array.push( [ 'CostanzoRecipes_Beverages_HotCocoa', '<p><ul><li onclick="handleItemClick()">Boil 8 oz boiling water </li><li onclick="handleItemClick()">4 TBS (¼ cup, 30g) powdered Whole milk  </li><li onclick="handleItemClick()">2 or up to 3 TBS (30-45g) cane sugar (or brown) * </li><li onclick="handleItemClick()">Add 3 TBS (27g) full fat cocoa </li><li onclick="handleItemClick()">Stir like mad! If it ain’t mixed now, too late. </li><li onclick="handleItemClick()">Now, the balance is up to you, I add 4 oz. ½ and ½ </li><li onclick="handleItemClick()">...or you can add 4 oz. milk. </li><li onclick="handleItemClick()">Heat the whole mixture and serve. </li></ul><p>​* This recipe is awfully sweet. Consider using less sugar: perhaps 20-25g. ' ] );
credits_array.push( [ 'CostanzoRecipes_Beverages_HotCocoa', '<p><ul><li onclick="handleItemClick()">date created: [2019-12-29 Sun]  </li><li onclick="handleItemClick()">From Todd McCagg </li><li onclick="handleItemClick()">[2020-03-18 Wed] I use brown sugar, but keep it toward the lower end. I use half half-and-half and half almond milk. Miss T seems to like it! </li></ul>' ] );
ingredients_array.push( [ 'CostanzoRecipes_Beverages_ChaiTea', '<p><ul><li onclick="handleItemClick()">1 Tblsp fennel or anise seed </li><li onclick="handleItemClick()">6 green cardamom pods </li><li onclick="handleItemClick()">12 cloves </li><li onclick="handleItemClick()">1 cinnamon stick </li><li onclick="handleItemClick()">&frac14;inch ginger root, sliced thinly </li><li onclick="handleItemClick()">&frac14;teasp black pepper corns </li><li onclick="handleItemClick()">2 bay leaves </li><li onclick="handleItemClick()">7 cups water </li><li onclick="handleItemClick()">2 Tblsp Darjeeling tea </li><li onclick="handleItemClick()">3 Tblsp honey or brown sugar (was 6) </li><li onclick="handleItemClick()">1 cup milk </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Beverages_ChaiTea', '<p><ol><li onclick="handleItemClick()">Boil fennel, cardamom, cloves, cinnamon, ginger root, pepper corns, bay leaves and water for 5 minutes, then steep for 10 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1010\')" class="alarm-clock-icon"><div id="circular-timer-1010" class="circular-timer-container"><script>new CircularTimer("circular-timer-1010", 300)</script><script>new CircularTimer("circular-timer-1010", 600)</script></li><li onclick="handleItemClick()">Add tea, honey and milk. Bring to a boil and simmer 5 minutes. Strain. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1011\')" class="alarm-clock-icon"><div id="circular-timer-1011" class="circular-timer-container"><script>new CircularTimer("circular-timer-1011", 300)</script></li></ol>' ] );
