ingredients_array.push( [ 'CostanzoRecipes_Appetizers_EasyRedPepperHummus', '<p>Serves 8 <p><ul><li onclick="handleItemClick()">1 (16 ounce) can garbanzo beans, drained and rinsed </li><li onclick="handleItemClick()">1 tablespoon olive oil </li><li onclick="handleItemClick()">1 medium red bell pepper, cut into ½ inch pieces </li><li onclick="handleItemClick()">1 tablespoon tahini </li><li onclick="handleItemClick()">1 fresh lime, juiced </li><li onclick="handleItemClick()">1½ tablespoons water </li><li onclick="handleItemClick()">½ teaspoon salt </li><li onclick="handleItemClick()">¼ teaspoon ground black pepper </li><li onclick="handleItemClick()">¼ teaspoon garlic powder </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Appetizers_EasyRedPepperHummus', '<p><ol><li onclick="handleItemClick()">In a food processor or blender, mix the garbanzo beans, olive oil, red bell pepper, tahini, lime juice, water, salt, black pepper, and garlic powder. Blend until smooth.  </li></ol>' ] );
