ingredients_array.push( [ 'CostanzoRecipes_Appetizers_CauliflowerPopcorn', '<p><ul><li onclick="handleItemClick()">2 pounds cauliflower, cut into ½- to 1-inch pieces and florets </li><li onclick="handleItemClick()">⅓ cup plus 1 tablespoon extra-virgin olive oil (or melted ghee) </li><li onclick="handleItemClick()">¼ cup nutritional yeast, divided </li><li onclick="handleItemClick()">¼ teaspoon red-pepper flakes or pinch of ground cayenne </li><li onclick="handleItemClick()">Kosher salt and black pepper </li><li onclick="handleItemClick()">½ cup panko bread crumbs </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Appetizers_CauliflowerPopcorn', '<p><ol><li onclick="handleItemClick()">Heat the oven to 450°. </li><li onclick="handleItemClick()">On a sheet pan, toss the cauliflower with ⅓ cup olive oil, 1 tablespoon nutritional yeast and the red-pepper flakes. </li><li onclick="handleItemClick()">Season generously with salt and pepper, then toss well to evenly distribute. </li><li onclick="handleItemClick()">Spread in an even layer and roast for 20 minutes, until the cauliflower is crisp-tender and starting to brown at the edges. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1003\')" class="alarm-clock-icon"><div id="circular-timer-1003" class="circular-timer-container"><script>new CircularTimer("circular-timer-1003", 1200)</script></li><li onclick="handleItemClick()">Meanwhile, in a small bowl, stir together the panko, remaining 3 tablespoons nutritional yeast and remaining tablespoon olive oil. </li><li onclick="handleItemClick()">Season with salt and pepper, and stir with a fork until evenly combined. </li><li onclick="handleItemClick()">When the 20 minutes are up, stir the cauliflower, spread it into an even layer and sprinkle with the panko mixture. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1004\')" class="alarm-clock-icon"><div id="circular-timer-1004" class="circular-timer-container"><script>new CircularTimer("circular-timer-1004", 1200)</script></li><li onclick="handleItemClick()">Roast until the panko is toasted, 10 to 12 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1005\')" class="alarm-clock-icon"><div id="circular-timer-1005" class="circular-timer-container"><script>new CircularTimer("circular-timer-1005", 600)</script><script>new CircularTimer("circular-timer-1005", 720)</script></li></ol>' ] );
credits_array.push( [ 'CostanzoRecipes_Appetizers_CauliflowerPopcorn', '<p><ul><li onclick="handleItemClick()">From <a href="https://cooking.nytimes.com/recipes/1022966-cauliflower-popcorn" target="_blank">https://cooking.nytimes.com/recipes/1022966-cauliflower-popcorn</a> </li><li onclick="handleItemClick()">date created: [2023-02-20 Mon]  </li><li onclick="handleItemClick()">Serves 4. </li><li onclick="handleItemClick()">[2023-02-18 Sat] Made for T, Greg and MaryAnn. They said they liked it a lot. </li></ul>' ] );
ingredients_array.push( [ 'CostanzoRecipes_Appetizers_CaramelizedOnionandSunDriedTomatoDip', '<p>Makes 1 cup; serves 4-6 <p><ul><li onclick="handleItemClick()">2 tablespoons	olive oil </li><li onclick="handleItemClick()">1 tablespoon	butter </li><li onclick="handleItemClick()">4 cups		sliced mild onions </li><li onclick="handleItemClick()">¼ teaspoon	dried rosemary, crumbled </li><li onclick="handleItemClick()">⅛ teaspoon	dried thyme </li><li onclick="handleItemClick()">⅓ cup		water </li><li onclick="handleItemClick()">2 tablespoons	white wine </li><li onclick="handleItemClick()">6			oil-marinated sun-dried tomato halves </li><li onclick="handleItemClick()">1 tablespoon	chopped fresh parsley </li><li onclick="handleItemClick()">½ teaspoon	capers (optional!) </li><li onclick="handleItemClick()">6 small		pitted black olives (For best results use a good flavorful olive such as a kalamata.) </li></ul>' ] );
methods_array.push( [ 'CostanzoRecipes_Appetizers_CaramelizedOnionandSunDriedTomatoDip', '<p><ol><li onclick="handleItemClick()">In a large skillet over medium-high heat, heat the oil and the butter. </li><li onclick="handleItemClick()">Add the onion; cook, stirring until softened about 2 minutes. <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1001\')" class="alarm-clock-icon"><div id="circular-timer-1001" class="circular-timer-container"><script>new CircularTimer("circular-timer-1001", 120)</script></li><li onclick="handleItemClick()">Stir in the the rosemary and thyme. </li><li onclick="handleItemClick()">Lower the heat and cook slowly, uncovered, until onions are golden, stirring occasionally, about 45 minutes. [Don\'t try to hurry the onions; they\'ll know this and won\'t caramelize! -jc] <img src="images/alarm-clock.png" onclick="toggleElement(\'circular-timer-1002\')" class="alarm-clock-icon"><div id="circular-timer-1002" class="circular-timer-container"><script>new CircularTimer("circular-timer-1002", 2700)</script></li><li onclick="handleItemClick()">Add the water, wine, sun-dried tomatoes, parsley and capers (if you have to!). Bring to a boil. </li><li onclick="handleItemClick()">Remove from heat and transfer to a food processor container fitted with a steel blade. </li><li onclick="handleItemClick()">Add the olives. Cover and process until everything is finely chopped. </li><li onclick="handleItemClick()">Variation: Substitute water for the wine. </li></ol>' ] );
credits_array.push( [ 'CostanzoRecipes_Appetizers_CaramelizedOnionandSunDriedTomatoDip', '<p><ul><li onclick="handleItemClick()">date created: [2007-06-27 Wed]  </li><li onclick="handleItemClick()">From a recipe appearing on page 65 of the book "1,000 Vegetarian Recipes" by Carol Gelles. </li><li onclick="handleItemClick()">[2002-11-01 Fri] Served by John and Tracey at their house in November, 2002. </li><li onclick="handleItemClick()">[2021-07-16 Fri] Made for Aron Cook\'s 75<sup>th</sup>birthday. </li></ul>' ] );
